CREATE OR REPLACE PROCEDURE cpoe_gerar_json_atendimento (nm_usuario_p text, dt_referencia_p timestamp, nr_atendimento_p bigint, nr_prescricao_json_p bigint) AS $body$
DECLARE


count_w integer;


BEGIN

if (coalesce(nr_prescricao_json_p,0) > 0) then
	begin
		--Deletar pois json tem que estar limpo caso não exista precrição.
		delete 	from cpoe_json_item
		where 	nr_atendimento = nr_atendimento_p
		and		dt_referencia = trunc(dt_referencia_p);

		select count(*)
		into STRICT count_w
		from prescr_medica
		where nr_prescricao = nr_prescricao_json_p
		and dt_prescricao between trunc(dt_referencia_p) and fim_dia(dt_referencia_p);

		if (coalesce(count_w,0) > 0) then
			CALL CPOE_Gerar_Registro_Json_PCK.CPOE_Gerar_Registros_Json(nr_atendimento_p ,dt_referencia_p,nm_usuario_p, nr_prescricao_json_p);
		end if;
	exception
	when others then
		null;
	end;

elsif (coalesce(nr_atendimento_p,0) > 0) then
	begin
		CALL CPOE_Gerar_Registro_Json_PCK.CPOE_Gerar_Registros_Json(nr_atendimento_p, dt_referencia_p,nm_usuario_p);
	exception
	when others then
		null;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
