SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_gerar_motivo_suspensao (nr_sequencia_p bigint, ie_tipo_item_p text, nr_seq_motivo_susp_p bigint, ds_motivo_susp_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (coalesce(nr_sequencia_p,0) > 0) then 
	 
	if	((ie_tipo_item_p = 'M') or (ie_tipo_item_p = 'MAT')) then /*Medicamento e Solução*/
 
		 
		update cpoe_material 
		set nr_seq_motivo_susp = nr_seq_motivo_susp_p, 
			ds_motivo_susp = ds_motivo_susp_p 
		where nr_sequencia = nr_sequencia_p;
		 
	elsif (ie_tipo_item_p = 'R') then 
		 
		update cpoe_recomendacao 
		set nr_seq_motivo_susp = nr_seq_motivo_susp_p, 
			ds_motivo_susp = ds_motivo_susp_p 
		where nr_sequencia = nr_sequencia_p;
		 
	elsif (ie_tipo_item_p = 'P') then 
		 
		update cpoe_procedimento 
		set nr_seq_motivo_susp = nr_seq_motivo_susp_p, 
			ds_motivo_susp = ds_motivo_susp_p 
		where nr_sequencia = nr_sequencia_p;
		 
	elsif	((ie_tipo_item_p = 'N') or (ie_tipo_item_p = 'S')) then--Dietas 
 
		update cpoe_dieta 
		set nr_seq_motivo_susp = nr_seq_motivo_susp_p, 
			ds_motivo_susp = ds_motivo_susp_p 
		where nr_sequencia = nr_sequencia_p;
		 
	 elsif (ie_tipo_item_p = 'AP') then --Anatomia Patologica 
		 
		update cpoe_anatomia_patologica 
		set nr_seq_motivo_susp = nr_seq_motivo_susp_p, 
			ds_motivo_susp = ds_motivo_susp_p 
		where nr_sequencia = nr_sequencia_p;
   
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
