SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_gerar_parse_xml ( nm_usuario_p text, nm_tabela_p text, ds_xml_p text) AS $body$
DECLARE

					 
nr_sequencia_w		cpoe_parse_xml.nr_sequencia%type;					

BEGIN 
 
if (ds_xml_p IS NOT NULL AND ds_xml_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then 
	 
	select	nextval('cpoe_parse_xml_seq') 
	into STRICT	nr_sequencia_w 
	;
	 
	insert into cpoe_parse_xml( 
					nr_sequencia, 
					nm_usuario, 
					nm_usuario_nrec, 
					dt_atualizacao, 
					dt_atualizacao_nrec, 
					ds_xml) 
				values ( 
					nr_sequencia_w, 
					nm_usuario_p, 
					nm_usuario_p, 
					clock_timestamp(), 
					clock_timestamp(), 
					xmlparse(DOCUMENT, convert_from(, 'utf-8')));
					 
		commit;
	 
	if (upper(nm_tabela_p) = 'CPOE_RECOMENDACAO') then 
		CALL cpoe_add_fav_recomendacao( nr_sequencia_w, nm_usuario_p);
	elsif (upper(nm_tabela_p) = 'CPOE_PROCEDIMENTO') then 
		CALL cpoe_add_fav_procedimento( nr_sequencia_w, nm_usuario_p);
	elsif (upper(nm_tabela_p) = 'CPOE_GASOTERAPIA') then 
		CALL cpoe_add_fav_gasoterapia( nr_sequencia_w, nm_usuario_p);
	elsif (upper(nm_tabela_p) = 'CPOE_MATERIAL') then 
		CALL cpoe_add_fav_material( nr_sequencia_w, nm_usuario_p);
	elsif (upper(nm_tabela_p) = 'CPOE_DIETA') then 
		CALL cpoe_add_fav_dieta( nr_sequencia_w, nm_usuario_p);
	end if;
	 
	delete from cpoe_parse_xml;
	 
	commit;
	 
end if;
 
end;
$body$
