SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_gerar_prot_dieta_oral ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type, nm_usuario_p usuario.nm_usuario%type, cd_protocolo_p protocolo_medic_dieta.cd_protocolo%type, nr_seq_protocolo_p protocolo_medic_dieta.nr_sequencia%type, nr_seq_item_p protocolo_medic_dieta.nr_seq_dieta%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_item_gerado_p INOUT cpoe_dieta.nr_sequencia%type, cd_pessoa_fisica_p cpoe_dieta.cd_pessoa_fisica%type, nr_seq_pend_pac_acao_p gqa_pend_pac_acao.nr_sequencia%type default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, nr_seq_transcricao_p bigint default null, nr_seq_conclusao_apae_p bigint default null, ie_forma_geracao_p text default null, dt_fim_presc_p timestamp default null, dt_inicio_presc_p timestamp default null, ie_futuro_p text default 'N', nr_seq_cpoe_rp_p cpoe_rp.nr_sequencia%type default null, nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) AS $body$
DECLARE

						
ds_stack_w				log_cpoe.ds_stack%type;
ds_log_cpoe_w			log_cpoe.ds_log%type;
cd_dieta_w				protocolo_medic_dieta.cd_dieta%type;
ie_destino_dieta_w		protocolo_medic_dieta.ie_destino_dieta%type;
ie_refeicao_w			protocolo_medic_dieta.ie_refeicao%type;
ds_horarios_w			protocolo_medic_dieta.ds_horarios%type;
ds_horarios_aux_w		protocolo_medic_dieta.ds_horarios%type;
ds_observacao_w			protocolo_medic_dieta.ds_observacao%type;
cd_intervalo_w			protocolo_medic_dieta.cd_intervalo%type;
ie_duracao_w			protocolo_medic_dieta.ie_duracao%type;
cd_intervalo_agora_w	protocolo_medic_material.cd_intervalo%type;

dt_inicio_w				cpoe_dieta.dt_inicio%type;
hr_prim_horario_w		cpoe_dieta.hr_prim_horario%type;
dt_fim_w				cpoe_dieta.dt_fim%type;
ie_evento_unico_w		cpoe_dieta.ie_evento_unico%type;

ie_dose_unica_cpoe_w	intervalo_prescricao.ie_dose_unica_cpoe%type;
qt_min_intervalo_w		intervalo_prescricao.qt_min_intervalo%type;
nr_ocorrencia_w			intervalo_prescricao.nr_etapas%type;

ie_urgencia_w			char(1):=null;
ie_prescr_alta_agora_w			varchar(1);

ie_param30_cadastro_prot_w  funcao_param_usuario.vl_parametro%type;
nr_seq_staple_food_w        protocolo_medic_dieta.nr_seq_staple_food%type;
nr_seq_beverage_w           protocolo_medic_dieta.nr_seq_beverage%type;
nr_seq_perfil_cal_w         protocolo_medic_dieta.nr_seq_perfil_cal%type;
nr_inicio_intervalo_w       bigint;
nr_fim_intervalo_w          bigint;

c01 CURSOR FOR						
SELECT	a.cd_dieta,
		a.ie_destino_dieta,
		a.ie_refeicao,
		a.ds_observacao,
		a.cd_intervalo,
		coalesce(a.ie_duracao,'C'),
		a.nr_seq_staple_food,
		a.nr_seq_beverage,
		a.nr_seq_perfil_cal
from	protocolo_medic_dieta a
where	a.nr_sequencia = nr_seq_protocolo_p
and		a.cd_protocolo = cd_protocolo_p
and		a.nr_seq_dieta = coalesce(nr_seq_item_p,a.nr_seq_dieta);


BEGIN
ie_prescr_alta_agora_w := obter_param_usuario(924, 409, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_prescr_alta_agora_w);
ie_param30_cadastro_prot_w := obter_param_usuario(99024, 30, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_param30_cadastro_prot_w);

open c01;
loop
fetch c01 into	
	cd_dieta_w,
	ie_destino_dieta_w,
	ie_refeicao_w,
	ds_observacao_w,
	cd_intervalo_w,
	ie_duracao_w,
	nr_seq_staple_food_w,         
	nr_seq_beverage_w,           
	nr_seq_perfil_cal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (dt_inicio_presc_p IS NOT NULL AND dt_inicio_presc_p::text <> '') then
		dt_inicio_w := dt_inicio_presc_p;	
	else	
		dt_inicio_w := trunc(clock_timestamp(),'hh24');	
	end if;		
	
	dt_fim_w := null;

	if (coalesce(obter_se_marca_agora(cpoe_obter_setor_atend_prescr(nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p)),'N') = 'S') then
		cd_intervalo_agora_w := cpoe_busca_intervalo_agora(nr_atendimento_p,'13',cd_estabelecimento_p,cd_perfil_p, nm_usuario_p);
	
		if (cd_intervalo_agora_w IS NOT NULL AND cd_intervalo_agora_w::text <> '') then
			cd_intervalo_w := cd_intervalo_agora_w;
			ie_urgencia_w  := '0';
		end if;
	end if;			

	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '') then
		select	max(ie_dose_unica_cpoe)
		into STRICT	ie_dose_unica_cpoe_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		if (coalesce(ie_dose_unica_cpoe_w, 'N') = 'S') then
			ie_duracao_w := 'P';
			ie_evento_unico_w := 'S';
		end if;
	end if;

 if ( UPPER(ie_param30_cadastro_prot_w) = 'Y') then
    select
      pmd.cd_intervalo,
      (pmd.ie_inicio_intervalo)::numeric ,
      (pmd.ie_fim_intervalo)::numeric 
    into STRICT  
      cd_intervalo_w,
      nr_inicio_intervalo_w,
      nr_fim_intervalo_w
    from    
      protocolo_medic_dieta pmd
      where	pmd.nr_sequencia = nr_seq_protocolo_p
      and		pmd.cd_protocolo = cd_protocolo_p
      and		pmd.nr_seq_dieta = coalesce(nr_seq_item_p,pmd.nr_seq_dieta);

    ie_urgencia_w := null;
    SELECT * FROM cpoe_gerar_interv_dieta_prot(nr_inicio_intervalo_w, nr_fim_intervalo_w, ie_duracao_w, dt_inicio_w, dt_fim_w) INTO STRICT ie_duracao_w, dt_inicio_w, dt_fim_w;
	end if;

	if (dt_inicio_w <= clock_timestamp()) then
		dt_inicio_w := dt_inicio_w +1/24;
	end if;
	
	if (coalesce(dt_inicio_presc_p::text, '') = '') and (cpoe_obter_hor_setor_ps(nm_usuario_p, cd_estabelecimento_p, nr_atendimento_p, cd_perfil_p, cd_intervalo_w) = 'S') then
		
		dt_inicio_w := clock_timestamp();
		ie_urgencia_w := '0';
		hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');
	else
		
		if (coalesce(dt_inicio_presc_p::text, '') = '') then
			hr_prim_horario_w  := substr(cpoe_obter_primeiro_horario(nr_atendimento_p, cd_intervalo_w, null, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p, cd_pessoa_fisica_p),1,5);
		end if;
		
		if (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
			dt_inicio_w	:= trunc(ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_inicio_w, hr_prim_horario_w),'mi');				
		end if;
		
		if (dt_inicio_w < clock_timestamp()) then
			dt_inicio_w := dt_inicio_w + 1;
		end if;
		
		hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');
	end if;

	select	coalesce(max(qt_min_intervalo),0)
	into STRICT	qt_min_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_w;
	
	nr_ocorrencia_w 	:= 0;
	ds_horarios_w		:= '';
	ds_horarios_aux_w	:= '';
	SELECT * FROM cpoe_calcular_horario_prescr(	nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
	ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;

	if (coalesce(ds_horarios_w::text, '') = '') then
		ds_horarios_w := hr_prim_horario_w;
	end if;
	
	if (ie_urgencia_w  = '0') then
		hr_prim_horario_w :=  to_char(clock_timestamp(),'hh24:mi');
		ds_horarios_w := hr_prim_horario_w;
	end if;	
	
if ((ie_duracao_w = 'P') and (UPPER(ie_param30_cadastro_prot_w) <> 'Y')) then
		dt_fim_w := ((dt_inicio_w + 1) +(1/86400));
	end if;

	if (dt_fim_presc_p IS NOT NULL AND dt_fim_presc_p::text <> '') then
		dt_fim_w := dt_fim_presc_p;
		ie_duracao_w := 'P';
	end if;

	if (coalesce(ie_prescr_alta_agora_w,'N') = 'S') and (coalesce(ie_item_alta_p,'N') = 'S')	then	

		ie_urgencia_w := 0;		

		select	max(qt_min_intervalo)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, null, clock_timestamp(), 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;

		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;

		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);

		dt_inicio_w	:= trunc(ESTABLISHMENT_TIMEZONE_UTILS.todayAtTime(hr_prim_horario_w),'mi');

		if (dt_inicio_w < clock_timestamp()) then

			dt_inicio_w := dt_inicio_w + 1;

		end if;
	end if;
	
	if (ie_retrogrado_p = 'S' or ie_futuro_p = 'S') then -- retrograde/backward/future item
		dt_inicio_w := dt_inicio_p;
		dt_fim_w := (dt_inicio_w + 1) - 1/1440;
		ie_duracao_w := 'P';
		ie_urgencia_w := null;	
		
		select	coalesce(max(qt_min_intervalo),0)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_w;
		
		nr_ocorrencia_w 	:= 0;
		ds_horarios_w		:= '';
		ds_horarios_aux_w	:= '';
		SELECT * FROM cpoe_calcular_horario_prescr(	nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;
		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);
	end if;
	
	select	nextval('cpoe_dieta_seq')
	into STRICT	nr_seq_item_gerado_p
	;
	
	insert into cpoe_dieta(
			nr_sequencia,
			nr_atendimento,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			ie_tipo_dieta,
			dt_inicio,
			hr_prim_horario,
			cd_intervalo,
			ds_horarios,
			nr_ocorrencia,
			ie_acm,
			ie_se_necessario,
			cd_dieta,
			ds_observacao,
			ie_administracao,
			ie_duracao,
			cd_perfil_ativo,
			cd_pessoa_fisica,
			cd_funcao_origem,
			ie_urgencia,
			nr_seq_pend_pac_acao,
			dt_fim,
			ie_item_alta,
			ie_prescritor_aux,
			cd_medico,
			ie_retrogrado,
			ie_futuro,
			nr_seq_pepo,
			nr_cirurgia,
			nr_cirurgia_patologia,
			nr_seq_agenda,
			nr_seq_transcricao,
			nr_seq_conclusao_apae,
			ie_forma_geracao,
			ie_evento_unico,
			nr_seq_cpoe_order_unit,
			nr_seq_staple_food,
			nr_seq_beverage,
			nr_seq_perfil_cal)
		values (
			nr_seq_item_gerado_p,
			nr_atendimento_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			'O',
			dt_inicio_w,
			hr_prim_horario_w,
			cd_intervalo_w,
			ds_horarios_w,
			nr_ocorrencia_w,
			'N',
			'N',
			cd_dieta_w,
			ds_observacao_w,
			'P',
			ie_duracao_w,
			cd_perfil_p,
			cd_pessoa_fisica_p,
			2314,
			ie_urgencia_w,
			nr_seq_pend_pac_acao_p,
			dt_fim_w,
			ie_item_alta_p,
			ie_prescritor_aux_p,
			cd_medico_p,
			ie_retrogrado_p,
			ie_futuro_p,
			nr_seq_pepo_p,
			nr_cirurgia_p,
			nr_cirurgia_patologia_p,
			nr_seq_agenda_p,
			nr_seq_transcricao_p,
			nr_seq_conclusao_apae_p,
			ie_forma_geracao_p,
			ie_evento_unico_w,
			nr_seq_cpoe_order_unit_p,
			nr_seq_staple_food_w,
			nr_seq_beverage_w,
			nr_seq_perfil_cal_w);

		CALL cpoe_gerar_plano_protocolo(nr_atendimento_p, dt_fim_presc_p, nr_seq_item_gerado_p, nm_usuario_p, 'N');

		CALL cpoe_utils_pck.set_list_itens(nr_seq_item_gerado_p, 'N');
	end;
end loop;
close c01;

commit;

exception
   when others then
	rollback;

	ds_stack_w	:= substr(dbms_utility.format_call_stack,1,2000);
	ds_log_cpoe_w := substr('CPOE_GERAR_PROT_DIETA_ORAL '
		|| chr(13) || ' - cd_estabelecimento_p: ' || cd_estabelecimento_p
		|| chr(13) || ' - cd_perfil_p: ' || cd_perfil_p
		|| chr(13) || ' - nm_usuario_p: ' || nm_usuario_p
		|| chr(13) || ' - cd_protocolo_p: ' || cd_protocolo_p
		|| chr(13) || ' - nr_seq_protocolo_p: ' || nr_seq_protocolo_p
		|| chr(13) || ' - nr_seq_item_p: ' || nr_seq_item_p
		|| chr(13) || ' - nr_atendimento_p: ' || nr_atendimento_p
		|| chr(13) || ' - nr_seq_item_gerado_p: ' || nr_seq_item_gerado_p
		|| chr(13) || ' - cd_pessoa_fisica_p: ' || cd_pessoa_fisica_p
		|| chr(13) || ' - nr_seq_pend_pac_acao_p: ' || nr_seq_pend_pac_acao_p
		|| chr(13) || ' - ie_item_alta_p: ' || ie_item_alta_p
		|| chr(13) || ' - ie_prescritor_aux_p: ' || ie_prescritor_aux_p
		|| chr(13) || ' - cd_medico_p: ' || cd_medico_p
		|| chr(13) || ' - ie_retrogrado_p: ' || ie_retrogrado_p
		|| chr(13) || ' - ie_futuro_p: ' || ie_futuro_p
		|| chr(13) || ' - dt_inicio_p: ' || PKG_DATE_FORMATERS_TZ.TO_VARCHAR(dt_inicio_p,'timestamp',ESTABLISHMENT_TIMEZONE_UTILS.gettimezone)
		|| chr(13) || ' - nr_seq_pepo_p: ' || nr_seq_pepo_p
		|| chr(13) || ' - nr_cirurgia_p: ' || nr_cirurgia_p
		|| chr(13) || ' - nr_cirurgia_patologia_p: ' || nr_cirurgia_patologia_p
		|| chr(13) || ' - nr_seq_agenda_p: ' || nr_seq_agenda_p
		|| chr(13) || ' - nr_seq_transcricao_p: ' || nr_seq_transcricao_p
		|| chr(13) || ' - nr_seq_conclusao_apae_p: ' || nr_seq_conclusao_apae_p
		|| chr(13) || ' - ie_forma_geracao_p: ' || ie_forma_geracao_p
		|| chr(13) || ' - dt_fim_presc_p: ' ||  PKG_DATE_FORMATERS_TZ.TO_VARCHAR(dt_fim_presc_p,'timestamp',ESTABLISHMENT_TIMEZONE_UTILS.gettimezone)
		|| chr(13) || ' - dt_inicio_presc_p: ' || PKG_DATE_FORMATERS_TZ.TO_VARCHAR(dt_inicio_presc_p,'timestamp',ESTABLISHMENT_TIMEZONE_UTILS.gettimezone)
		|| chr(13) || ' - ERRO: ' || sqlerrm
		|| chr(13) || ' - FUNCAO('||to_char(obter_funcao_ativa)||'); PERFIL('||to_char(obter_perfil_ativo)||')',1,2000);

	insert into log_cpoe(
		nr_sequencia,
		nr_atendimento, 
		dt_atualizacao, 
		nm_usuario, 
		ds_log, 
		ds_stack) 
	values (
		nextval('log_cpoe_seq'), 
		nr_atendimento_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_log_cpoe_w, 
		ds_stack_w);
		
	commit;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(sqlerrm);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_gerar_prot_dieta_oral ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type, nm_usuario_p usuario.nm_usuario%type, cd_protocolo_p protocolo_medic_dieta.cd_protocolo%type, nr_seq_protocolo_p protocolo_medic_dieta.nr_sequencia%type, nr_seq_item_p protocolo_medic_dieta.nr_seq_dieta%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_item_gerado_p INOUT cpoe_dieta.nr_sequencia%type, cd_pessoa_fisica_p cpoe_dieta.cd_pessoa_fisica%type, nr_seq_pend_pac_acao_p gqa_pend_pac_acao.nr_sequencia%type default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, nr_seq_transcricao_p bigint default null, nr_seq_conclusao_apae_p bigint default null, ie_forma_geracao_p text default null, dt_fim_presc_p timestamp default null, dt_inicio_presc_p timestamp default null, ie_futuro_p text default 'N', nr_seq_cpoe_rp_p cpoe_rp.nr_sequencia%type default null, nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) FROM PUBLIC;

