SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_gerar_prot_recomendacao ( cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p cpoe_recomendacao.nm_usuario%type, cd_protocolo_p protocolo_medic_rec.cd_protocolo%type, nr_seq_protocolo_p protocolo_medic_rec.nr_sequencia%type, nr_seq_item_p protocolo_medic_rec.nr_sequencia%type, nr_atendimento_p cpoe_recomendacao.nr_atendimento%type, cd_pessoa_fisica_p cpoe_recomendacao.cd_pessoa_fisica%type, nr_seq_item_gerado_p INOUT cpoe_recomendacao.nr_sequencia%type, nr_seq_pend_pac_acao_p gqa_pend_pac_acao.nr_sequencia%type default null, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, ie_oncologia_p text default 'N', nr_seq_conclusao_apae_p bigint default null, dt_fim_presc_p timestamp default null, dt_inicio_presc_p timestamp default null, ie_futuro_p text default 'N', nr_seq_cpoe_rp_p cpoe_rp.nr_sequencia%type default null, nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) AS $body$
DECLARE


ds_stack_w					log_cpoe.ds_stack%type;
ds_log_cpoe_w				log_cpoe.ds_log%type;
ds_recomendacao_w			protocolo_medic_rec.ds_recomendacao%type;
cd_recomendacao_w			protocolo_medic_rec.cd_recomendacao%type;
cd_intervalo_w				protocolo_medic_rec.cd_intervalo%type;
cd_intervalo_agora_w		protocolo_medic_rec.cd_intervalo%type;
nr_seq_topografia_w			protocolo_medic_rec.nr_seq_topografia%type;
ie_duracao_w				protocolo_medic_rec.ie_duracao%type;

qt_min_intervalo_w			intervalo_prescricao.qt_min_intervalo%type;
nr_ocorrencia_w				intervalo_prescricao.qt_operacao%type;
ie_exibe_w					intervalo_prescricao.cd_intervalo%type;
ie_dose_unica_cpoe_w		intervalo_prescricao.ie_dose_unica_cpoe%type := 'N';

dt_fim_w					cpoe_recomendacao.dt_fim%type;
ie_evento_unico_w			cpoe_recomendacao.ie_evento_unico%type;

dt_inicio_w					timestamp;
dt_inicio_base_w			timestamp;
hr_prim_horario_w			char(5);
ie_acm_w					char(1);
ie_se_necessario_w			char(1);
ie_urgencia_w				char(1);
ie_administracao_w			char(1);
ds_horarios_w				varchar(4000);
ds_horarios_aux_w			varchar(4000);
ie_prescr_alta_agora_w		varchar(1);


c01 CURSOR FOR
SELECT	a.ds_recomendacao,
		a.cd_recomendacao,
		a.cd_intervalo,
		a.ds_horarios,
		coalesce(a.ie_acm,'N'),
		coalesce(a.ie_se_necessario,'N'),
		coalesce(a.ie_urgencia,'N'),
		a.nr_seq_topografia,
		coalesce(a.ie_duracao,'C')
from	protocolo_medic_rec a
where	a.nr_sequencia = nr_seq_protocolo_p
and		a.cd_protocolo = cd_protocolo_p
and		a.nr_seq_rec = coalesce(nr_seq_item_p,a.nr_seq_rec)
order by coalesce(nr_seq_apres,999);
		

BEGIN
ie_prescr_alta_agora_w := obter_param_usuario(924, 409, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_prescr_alta_agora_w);

if (dt_inicio_presc_p IS NOT NULL AND dt_inicio_presc_p::text <> '') then
	dt_inicio_base_w := dt_inicio_presc_p;
else
	dt_inicio_base_w := trunc(clock_timestamp(),'mi');
end if;

open c01;
loop
fetch c01 into	
	ds_recomendacao_w,
	cd_recomendacao_w,
	cd_intervalo_w,
	ds_horarios_w,
	ie_acm_w,
	ie_se_necessario_w,
	ie_urgencia_w,
	nr_seq_topografia_w,
	ie_duracao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	dt_fim_w := null;

	if ((coalesce(dt_inicio_presc_p::text, '') = '') and (coalesce(obter_se_marca_agora(cpoe_obter_setor_atend_prescr(nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p)),'N') = 'S')) then
		cd_intervalo_agora_w := cpoe_busca_intervalo_agora(nr_atendimento_p,'R',cd_estabelecimento_p,cd_perfil_p, nm_usuario_p);
		
		if (cd_intervalo_agora_w IS NOT NULL AND cd_intervalo_agora_w::text <> '') then
			cd_intervalo_w := cd_intervalo_agora_w;
			ie_urgencia_w := 'S';
		end if;
		
	end if;		

	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '') then
		select	max(ie_dose_unica_cpoe)
		into STRICT	ie_dose_unica_cpoe_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		if (coalesce(ie_dose_unica_cpoe_w, 'N') = 'S') then
			ie_duracao_w := 'P';
			ie_evento_unico_w := 'S';
			ie_urgencia_w := 'S';
		end if;
	end if;

	if (ie_urgencia_w = 'S') then
		dt_inicio_w			:= dt_inicio_base_w;
		ie_urgencia_w		:= '0';
		ie_acm_w			:= 'N';
		ie_se_necessario_w	:= 'N';
	else
		ie_urgencia_w		:= null;
		
		if (coalesce(dt_inicio_presc_p::text, '') = '') and (cpoe_obter_hor_setor_ps(nm_usuario_p, cd_estabelecimento_p, nr_atendimento_p, cd_perfil_p, cd_intervalo_w) = 'S') then
		
			dt_inicio_w := clock_timestamp();
			ie_urgencia_w := '0';
			hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');
		else
			
			if (coalesce(dt_inicio_presc_p::text, '') = '') then
				dt_inicio_w := trunc(dt_inicio_base_w+1/24,'hh24');
				hr_prim_horario_w  := substr(cpoe_obter_primeiro_horario(nr_atendimento_p, cd_intervalo_w, null, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p, cd_pessoa_fisica_p),1,5);
			else
				dt_inicio_w := dt_inicio_base_w;
			end if;
			
			if (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
				dt_inicio_w	:= ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_inicio_w, hr_prim_horario_w);				
			end if;
			
			if (dt_inicio_w < clock_timestamp()) then
				dt_inicio_w := dt_inicio_w + 1;
			end if;
		end if;
	end if;
	
	hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');
	
	if (ie_acm_w = 'N') and (ie_se_necessario_w = 'N') and (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') and (coalesce(dt_inicio_presc_p::text, '') = '') then
		ds_horarios_w := reordenar_horarios_prescr(dt_inicio_w, ds_horarios_w);

		if (position(' ' in ds_horarios_w) > 0) then
			if (position(':' in ds_horarios_w) > 0) then
				hr_prim_horario_w := substr(ds_horarios_w,1,position(' ' in ds_horarios_w) - 1);
			else
				hr_prim_horario_w := substr(ds_horarios_w,1,position(' ' in ds_horarios_w) - 1)||':00';
			end if;
		else
			if (position(':' in ds_horarios_w) > 0) then
				hr_prim_horario_w := ds_horarios_w;
			else
				hr_prim_horario_w := ds_horarios_w||':00';
			end if;
		end if;

		dt_inicio_w := ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_inicio_w, hr_prim_horario_w);
		if (dt_inicio_w <= dt_inicio_base_w) then
			dt_inicio_w := dt_inicio_w + 1;
		end if;
		
		nr_ocorrencia_w	:= coalesce(obter_ocorrencias_horarios_rep(ds_horarios_w),0);
	else
		select	coalesce(max(qt_min_intervalo),0)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_w;
		
		nr_ocorrencia_w 	:= 0;
		ds_horarios_w		:= '';
		ds_horarios_aux_w	:= '';
		SELECT * FROM cpoe_calcular_horario_prescr(	nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;

		if (coalesce(ds_horarios_w::text, '') = '') then
			ds_horarios_w	:= hr_prim_horario_w;
			nr_ocorrencia_w	:= 1;
		end if;
	end if;
	
	ie_administracao_w := 'P';
	if (ie_se_necessario_w = 'S') then
		ie_administracao_w	:= 'N';
		ie_acm_w			:= 'N';
		ds_horarios_w		:= '';
		hr_prim_horario_w	:= null;
	elsif (ie_acm_w = 'S') then
		ie_administracao_w	:= 'C';
		ie_se_necessario_w	:= 'N';
		ds_horarios_w		:= '';
		hr_prim_horario_w	:= null;
	end if;
	
	if (ie_duracao_w = 'P') then
		dt_fim_w := ((dt_inicio_w + 1) +(1/86400));
	end if;
	
	if (dt_fim_presc_p IS NOT NULL AND dt_fim_presc_p::text <> '') then
		dt_fim_w := dt_fim_presc_p;
		ie_duracao_w := 'P';
	end if;	
	
	if (coalesce(ie_prescr_alta_agora_w,'N') = 'S') and (coalesce(ie_item_alta_p,'N') = 'S')	then	

		ie_urgencia_w := 0;		

		select	max(qt_min_intervalo)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, null, clock_timestamp(), 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;

		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;

		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);

		dt_inicio_w	:= ESTABLISHMENT_TIMEZONE_UTILS.todayAtTime(hr_prim_horario_w);

		if (dt_inicio_w < clock_timestamp()) then

			dt_inicio_w := dt_inicio_w + 1;

		end if;
	end if;
	if (ie_retrogrado_p = 'S' or ie_futuro_p = 'S') then -- retrograde/backward item
		dt_inicio_w := dt_inicio_p;
		dt_fim_w := (dt_inicio_w + 1) - 1/1440;
		
		select	coalesce(max(qt_min_intervalo),0)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;
		
		ie_duracao_w 		:= 'P';
		nr_ocorrencia_w 	:= 0;
		ie_urgencia_w 		:= null;		
		ds_horarios_w		:= '';
		ds_horarios_aux_w	:= '';
		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
									
		ds_horarios_w		:= ds_horarios_w || ds_horarios_aux_w;
		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);
	end if;
	
	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '')then
	-- esse select deve ser o mesmo que ata ativando o lookup de intervalo, qualquer alteracao aqui deve ser replicada para o looup e vice-versa
		SELECT coalesce(MAX(cd_intervalo),'N')
		INTO STRICT ie_exibe_w
		FROM intervalo_prescricao
		WHERE 	cd_intervalo = cd_intervalo_w
		AND 	ie_situacao = 'A'
		and	((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = cd_estabelecimento_p))
		and	Obter_se_intervalo(cd_intervalo_w,'R') = 'S'
		AND	obter_se_interv_regra(cd_intervalo_w, case to_char('R') when 'G' then 'GAS' when '15' then '15' when '8' then 'DE' end) = 'S'
		and cpoe_obter_se_exibe_interv(nr_atendimento_p,cd_estabelecimento_p,cd_intervalo_w,cd_perfil_p,nm_usuario_p) = 'S'
		and	((coalesce(ie_so_retrograda,'N') = 'N') or (coalesce(ie_retrogrado_p,'N') = 'N'));
		
		if (ie_exibe_w = 'N')then
			cd_intervalo_w := null;
		end if;
	end if;
	
	if (coalesce(cd_intervalo_w::text, '') = '')  or (cd_intervalo_w = '') then
			ds_horarios_w	:= '';
	end if;
	
	select	nextval('cpoe_recomendacao_seq')
	into STRICT	nr_seq_item_gerado_p
	;
	
	insert into cpoe_recomendacao(
				nr_sequencia,
				nr_atendimento,
				cd_pessoa_fisica,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				cd_recomendacao,
				ds_recomendacao,
				cd_intervalo,
				ds_horarios,
				hr_prim_horario,
				dt_inicio,
				ie_administracao,
				ie_urgencia,
				ie_se_necessario,
				ie_acm,
				nr_seq_topografia,
				nr_ocorrencia,
				ie_duracao,
				cd_perfil_ativo,
				cd_funcao_origem,
				nr_seq_pend_pac_acao,
				dt_fim,
				nr_seq_transcricao,
				ie_item_alta,
				ie_prescritor_aux,
				cd_medico,
				ie_retrogrado,
				ie_futuro,
				nr_seq_pepo,
				nr_cirurgia,
				nr_cirurgia_patologia,
				nr_seq_agenda,
				ie_oncologia,
				nr_seq_conclusao_apae,
				cd_protocolo,
				nr_seq_protocolo,
				ie_forma_geracao,
				ie_evento_unico,
				nr_seq_cpoe_order_unit)
			values (
				nr_seq_item_gerado_p,
				nr_atendimento_p,
				cd_pessoa_fisica_p,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				cd_recomendacao_w,
				ds_recomendacao_w,
				cd_intervalo_w,
				ds_horarios_w,
				hr_prim_horario_w,
				dt_inicio_w,
				ie_administracao_w,
				ie_urgencia_w,
				ie_se_necessario_w,
				ie_acm_w,
				nr_seq_topografia_w,
				nr_ocorrencia_w,
				ie_duracao_w,
				cd_perfil_p,
				2314,
				nr_seq_pend_pac_acao_p,
				dt_fim_w,
				nr_seq_transcricao_p,
				ie_item_alta_p,
				ie_prescritor_aux_p,
				cd_medico_p,
				ie_retrogrado_p,
				ie_futuro_p,
				nr_seq_pepo_p,
				nr_cirurgia_p,
				nr_cirurgia_patologia_p,
				nr_seq_agenda_p,
				ie_oncologia_p,
				nr_seq_conclusao_apae_p,
				cd_protocolo_p,
				nr_seq_protocolo_p,
				'P',
				ie_evento_unico_w,
				nr_seq_cpoe_order_unit_p);
	end;
	
	CALL cpoe_gerar_plano_protocolo(nr_atendimento_p, dt_fim_presc_p, nr_seq_item_gerado_p, nm_usuario_p, 'R');

    CALL cpoe_utils_pck.set_list_itens(nr_seq_item_gerado_p, 'R');
end loop;
close c01;

commit;

exception
   when others then
	rollback;

	ds_stack_w	:= substr(dbms_utility.format_call_stack,1,2000);
	ds_log_cpoe_w := substr('CPOE_GERAR_PROT_RECOMENDACAO '
		|| chr(13) || ' - cd_estabelecimento_p: ' || cd_estabelecimento_p
		|| chr(13) || ' - cd_perfil_p: ' || cd_perfil_p
		|| chr(13) || ' - nm_usuario_p: ' || nm_usuario_p
		|| chr(13) || ' - cd_protocolo_p: ' || cd_protocolo_p
		|| chr(13) || ' - nr_seq_protocolo_p: ' || nr_seq_protocolo_p
		|| chr(13) || ' - nr_seq_item_p: ' || nr_seq_item_p
		|| chr(13) || ' - nr_atendimento_p: ' || nr_atendimento_p
		|| chr(13) || ' - cd_pessoa_fisica_p: ' || cd_pessoa_fisica_p
		|| chr(13) || ' - nr_seq_item_gerado_p: ' || nr_seq_item_gerado_p
		|| chr(13) || ' - nr_seq_pend_pac_acao_p: ' || nr_seq_pend_pac_acao_p
		|| chr(13) || ' - nr_seq_transcricao_p: ' || nr_seq_transcricao_p
		|| chr(13) || ' - ie_item_alta_p: ' || ie_item_alta_p
		|| chr(13) || ' - ie_prescritor_aux_p: ' || ie_prescritor_aux_p
		|| chr(13) || ' - cd_medico_p: ' || cd_medico_p
		|| chr(13) || ' - ie_retrogrado_p: ' || ie_retrogrado_p
		|| chr(13) || ' - ie_futuro_p: ' || ie_futuro_p
		|| chr(13) || ' - dt_inicio_p: ' || PKG_DATE_FORMATERS_TZ.TO_VARCHAR(dt_inicio_p,'timestamp',ESTABLISHMENT_TIMEZONE_UTILS.gettimezone)
		|| chr(13) || ' - nr_seq_pepo_p: ' || nr_seq_pepo_p
		|| chr(13) || ' - nr_cirurgia_p: ' || nr_cirurgia_p
		|| chr(13) || ' - nr_cirurgia_patologia_p: ' || nr_cirurgia_patologia_p
		|| chr(13) || ' - nr_seq_agenda_p: ' || nr_seq_agenda_p
		|| chr(13) || ' - ie_oncologia_p: ' || ie_oncologia_p
		|| chr(13) || ' - nr_seq_conclusao_apae_p: ' || nr_seq_conclusao_apae_p
		|| chr(13) || ' - dt_fim_presc_p: ' || PKG_DATE_FORMATERS_TZ.TO_VARCHAR(dt_fim_presc_p,'timestamp',ESTABLISHMENT_TIMEZONE_UTILS.gettimezone)
		|| chr(13) || ' - dt_inicio_presc_p: ' || PKG_DATE_FORMATERS_TZ.TO_VARCHAR(dt_inicio_presc_p,'timestamp',ESTABLISHMENT_TIMEZONE_UTILS.gettimezone)
		|| chr(13) || ' - ERRO: ' || sqlerrm
		|| chr(13) || ' - FUNCAO('||to_char(obter_funcao_ativa)||'); PERFIL('||to_char(obter_perfil_ativo)||')',1,2000);
		
	insert into log_cpoe(
		nr_sequencia,
		nr_atendimento, 
		dt_atualizacao, 
		nm_usuario, 
		ds_log, 
		ds_stack) 
	values (
		nextval('log_cpoe_seq'), 
		nr_atendimento_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_log_cpoe_w, 
		ds_stack_w);
		
	commit;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(sqlerrm);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_gerar_prot_recomendacao ( cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p cpoe_recomendacao.nm_usuario%type, cd_protocolo_p protocolo_medic_rec.cd_protocolo%type, nr_seq_protocolo_p protocolo_medic_rec.nr_sequencia%type, nr_seq_item_p protocolo_medic_rec.nr_sequencia%type, nr_atendimento_p cpoe_recomendacao.nr_atendimento%type, cd_pessoa_fisica_p cpoe_recomendacao.cd_pessoa_fisica%type, nr_seq_item_gerado_p INOUT cpoe_recomendacao.nr_sequencia%type, nr_seq_pend_pac_acao_p gqa_pend_pac_acao.nr_sequencia%type default null, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, ie_oncologia_p text default 'N', nr_seq_conclusao_apae_p bigint default null, dt_fim_presc_p timestamp default null, dt_inicio_presc_p timestamp default null, ie_futuro_p text default 'N', nr_seq_cpoe_rp_p cpoe_rp.nr_sequencia%type default null, nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) FROM PUBLIC;

