SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_gerar_recomendacao_rotina ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_produto_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nr_seq_item_gerado_p INOUT bigint, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, ie_oncologia_p text default 'N', nr_seq_conclusao_apae_p bigint default null, cd_kit_p bigint default null, ie_futuro_p text default 'N', nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) AS $body$
DECLARE


ds_stack_w				log_cpoe.ds_stack%type;
ds_log_cpoe_w			log_cpoe.ds_log%type;
nr_seq_recomendacao_w	cpoe_recomendacao.nr_sequencia%type;
nr_ocorrencia_w			cpoe_recomendacao.nr_ocorrencia%type;
ie_duracao_w			cpoe_recomendacao.ie_duracao%type := 'C';
ie_evento_unico_w		cpoe_recomendacao.ie_evento_unico%type;

cd_intervalo_w			tipo_recomendacao.cd_intervalo%type;
cd_intervalo_agora_w	tipo_recomendacao.cd_intervalo%type;
cd_tipo_recomendacao_w	tipo_recomendacao.cd_tipo_recomendacao%type;
ds_tipo_recomendacao_w	tipo_recomendacao.ds_tipo_recomendacao%type;
ie_urgencia_w			tipo_recomendacao.ie_urgencia%type;
nr_seq_topografia_w		tipo_recomendacao.nr_seq_topografia%type;
ds_complemento_w        tipo_recomendacao.ds_complemento%type;

ie_exibe_w				intervalo_prescricao.cd_intervalo%type;
ie_dose_unica_cpoe_w	intervalo_prescricao.ie_dose_unica_cpoe%type;


ie_prescr_alta_agora_w	varchar(1);
ds_horarios_w			varchar(4000);
ds_horarios_aux_w		varchar(4000);
hr_prim_horario_w		char(5);
dt_inicio_w				timestamp;
dt_inicio_base_w		timestamp;
dt_fim_w				timestamp := null;

C01 CURSOR FOR
		SELECT	cd_intervalo,
			cd_tipo_recomendacao,
			ds_tipo_recomendacao,
			coalesce(ie_urgencia,'N') ie_urgencia,
			nr_seq_topografia,
            ds_complemento
		from	tipo_recomendacao
		where	cd_tipo_recomendacao = nr_seq_produto_p;
		

BEGIN

ie_prescr_alta_agora_w := obter_param_usuario(924, 409, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_prescr_alta_agora_w);
dt_inicio_base_w	:= trunc(clock_timestamp(),'mi');

open C01;
loop
fetch C01 into	
	cd_intervalo_w,
	cd_tipo_recomendacao_w,
	ds_tipo_recomendacao_w,
	ie_urgencia_w,
	nr_seq_topografia_w,
    ds_complemento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	if (ie_urgencia_w = 'S') then
		ie_urgencia_w  := '0';
	else
		ie_urgencia_w  := null;
		dt_inicio_base_w := trunc(clock_timestamp() + interval '1 days'/24,'hh24');
	end if;
	hr_prim_horario_w	:= to_char(dt_inicio_base_w,'hh24:mi');

	if (coalesce(obter_se_marca_agora(cpoe_obter_setor_atend_prescr(nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p)),'N') = 'S') then
		cd_intervalo_agora_w := cpoe_busca_intervalo_agora(nr_atendimento_p,'R',cd_estabelecimento_p,cd_perfil_p, nm_usuario_p);
		
		if (cd_intervalo_agora_w IS NOT NULL AND cd_intervalo_agora_w::text <> '') then
			cd_intervalo_w := cd_intervalo_agora_w;
			ie_urgencia_w  := '0';
			hr_prim_horario_w :=  to_char(clock_timestamp(),'hh24:mi');
		end if;
		
	end if;	

	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '') then
		select	max(ie_dose_unica_cpoe)
		into STRICT	ie_dose_unica_cpoe_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		if (coalesce(ie_dose_unica_cpoe_w, 'N') = 'S') then
			ie_duracao_w := 'P';
			ie_evento_unico_w := 'S';
		end if;
	end if;	
	
	if (coalesce(ie_prescr_alta_agora_w,'N') = 'S') and (coalesce(ie_item_alta_p,'N') = 'S')	then	
		ie_urgencia_w  := '0';	
		hr_prim_horario_w :=  to_char(clock_timestamp(),'hh24:mi');			
	end if;	
	
	if (coalesce(ie_urgencia_w,'XPTO') <> '0') then
		hr_prim_horario_w	:= coalesce(substr(cpoe_obter_primeiro_horario( nr_atendimento_p, cd_intervalo_w, null, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p, cd_pessoa_fisica_p),1,5), hr_prim_horario_w);
	end if;
	
	dt_inicio_w			:= to_date(to_char(dt_inicio_base_w,'dd/mm/yyyy ')  || hr_prim_horario_w, 'dd/mm/yyyy hh24:mi');
	
	if (ie_retrogrado_p = 'S' or ie_futuro_p = 'S') then -- retrograde/backward item
		dt_inicio_w := dt_inicio_p;
		dt_fim_w := (dt_inicio_w + 1) - 1/1440;
		hr_prim_horario_w :=  to_char(dt_inicio_w,'hh24:mi');	
		ie_duracao_w 		:= 'P';
		ie_urgencia_w 		:= null;
	end if;
	
	nr_ocorrencia_w		:= 0;
	ds_horarios_w		:= '';
	ds_horarios_aux_w	:= '';
	SELECT * FROM cpoe_calcular_horario_prescr(	nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, null, null, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
	ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;
	
	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '')then
	-- esse select deve ser o mesmo que ata ativando o lookup de intervalo, qualquer alteracao aqui deve ser replicada para o looup e vice-versa
		SELECT coalesce(MAX(cd_intervalo),'N')
		INTO STRICT ie_exibe_w
		FROM intervalo_prescricao
		WHERE 	cd_intervalo = cd_intervalo_w
		AND 	ie_situacao = 'A'
		and	((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = cd_estabelecimento_p))
		and	Obter_se_intervalo(cd_intervalo_w,'R') = 'S'
		AND	obter_se_interv_regra(cd_intervalo_w, case to_char('R') when 'G' then 'GAS' when '15' then '15' when '8' then 'DE' end) = 'S'
		and cpoe_obter_se_exibe_interv(nr_atendimento_p,cd_estabelecimento_p,cd_intervalo_w,cd_perfil_p,nm_usuario_p) = 'S'
		and	((coalesce(ie_so_retrograda,'N') = 'N') or (coalesce(ie_retrogrado_p,'N') = 'N'));
		
		if (ie_exibe_w = 'N')then
			cd_intervalo_w := null;
		end if;
	end if;
		
	if (ie_duracao_w = 'P') then
		dt_fim_w := (dt_inicio_w + 1) - 1/1440;
	end if;
	
	if (coalesce(cd_intervalo_w::text, '') = '')  or (cd_intervalo_w = '') then
			ds_horarios_w	:= '';
	end if;

	select	nextval('cpoe_recomendacao_seq')
	into STRICT	nr_seq_recomendacao_w
	;
	
	insert into cpoe_recomendacao(
			nr_sequencia,
			cd_intervalo,
			cd_recomendacao, 
			ds_recomendacao,
			ie_urgencia,
			ie_acm,
			ie_se_necessario,
			ie_administracao,
			nr_seq_topografia,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nr_atendimento,
			ds_horarios,
			dt_inicio,
			dt_fim,
			hr_prim_horario,
			nr_ocorrencia,
			ie_duracao,
			cd_perfil_ativo,
			cd_funcao_origem,
			cd_pessoa_fisica,
			nr_seq_transcricao,
			ie_item_alta,
			ie_prescritor_aux,
			cd_medico,
			ie_retrogrado,
			nr_seq_pepo,
			nr_cirurgia,
			nr_cirurgia_patologia,
			nr_seq_agenda,
			ie_oncologia,
			nr_seq_conclusao_apae,
			ie_evento_unico,
			cd_kit,
			ie_futuro,
			nr_seq_cpoe_order_unit)
		values (
			nr_seq_recomendacao_w,
			cd_intervalo_w,
			cd_tipo_recomendacao_w,
			ds_complemento_w,
			ie_urgencia_w,
			'N',
			'N',
			'P',
			nr_seq_topografia_w,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			nr_atendimento_p,
			ds_horarios_w,
			dt_inicio_w,
			dt_fim_w,
			hr_prim_horario_w,
			nr_ocorrencia_w,
			ie_duracao_w,
			cd_perfil_p,
			2314,
			cd_pessoa_fisica_p,
			nr_seq_transcricao_p,
			ie_item_alta_p,
			ie_prescritor_aux_p,
			cd_medico_p,
			ie_retrogrado_p,
			nr_seq_pepo_p,
			nr_cirurgia_p,
			nr_cirurgia_patologia_p,
			nr_seq_agenda_p,
			ie_oncologia_p,
			nr_seq_conclusao_apae_p,
			ie_evento_unico_w,
			cd_kit_p,
			ie_futuro_p,
			nr_seq_cpoe_order_unit_p);

	update w_kit_modificado
	set	   nr_seq_rec_cpoe = nr_seq_recomendacao_w
	where  ((nr_atendimento = nr_atendimento_p) or (coalesce(nr_atendimento,0) = 0))
	and	cd_recomendacao = cd_tipo_recomendacao_w
	and	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nm_usuario = nm_usuario_p;

	commit;		
	
	end;
end loop;
close C01;

nr_seq_item_gerado_p := nr_seq_recomendacao_w;

exception
   when others then
	rollback;

	ds_stack_w	:= substr(dbms_utility.format_call_stack,1,2000);
	ds_log_cpoe_w := substr('CPOE_GERAR_RECOMENDACAO_ROTINA '
		|| chr(13) || ' - nr_atendimento_p: ' || nr_atendimento_p
		|| chr(13) || ' - cd_pessoa_fisica_p: ' || cd_pessoa_fisica_p
		|| chr(13) || ' - nr_seq_produto_p: ' || nr_seq_produto_p
		|| chr(13) || ' - nm_usuario_p: ' || nm_usuario_p
		|| chr(13) || ' - cd_estabelecimento_p: ' || cd_estabelecimento_p
		|| chr(13) || ' - cd_perfil_p: ' || cd_perfil_p
		|| chr(13) || ' - nr_seq_item_gerado_p: ' || nr_seq_item_gerado_p
		|| chr(13) || ' - nr_seq_transcricao_p: ' || nr_seq_transcricao_p
		|| chr(13) || ' - ie_item_alta_p: ' || ie_item_alta_p
		|| chr(13) || ' - ie_prescritor_aux_p: ' || ie_prescritor_aux_p
		|| chr(13) || ' - cd_medico_p: ' || cd_medico_p
		|| chr(13) || ' - ie_retrogrado_p: ' || ie_retrogrado_p
		|| chr(13) || ' - ie_futuro_p: ' || ie_futuro_p
		|| chr(13) || ' - dt_inicio_p: ' || to_char(dt_inicio_p, 'dd/mm/yyyy hh24:mi:ss')
		|| chr(13) || ' - nr_seq_pepo_p: ' || nr_seq_pepo_p
		|| chr(13) || ' - nr_cirurgia_p: ' || nr_cirurgia_p
		|| chr(13) || ' - nr_cirurgia_patologia_p: ' || nr_cirurgia_patologia_p
		|| chr(13) || ' - nr_seq_agenda_p: ' || nr_seq_agenda_p
		|| chr(13) || ' - ie_oncologia_p: ' || ie_oncologia_p
		|| chr(13) || ' - nr_seq_conclusao_apae_p: ' || nr_seq_conclusao_apae_p
		|| chr(13) || ' - cd_kit_p: ' || cd_kit_p
		|| chr(13) || ' - ERRO: ' || sqlerrm
		|| chr(13) || ' - FUNCAO('||to_char(obter_funcao_ativa)||'); PERFIL('||to_char(obter_perfil_ativo)||')',1,2000);
		
	insert into log_cpoe(
		nr_sequencia,
		nr_atendimento, 
		dt_atualizacao, 
		nm_usuario, 
		ds_log, 
		ds_stack) 
	values (
		nextval('log_cpoe_seq'), 
		nr_atendimento_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_log_cpoe_w, 
		ds_stack_w);
		
	commit;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(sqlerrm);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_gerar_recomendacao_rotina ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_produto_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nr_seq_item_gerado_p INOUT bigint, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, ie_oncologia_p text default 'N', nr_seq_conclusao_apae_p bigint default null, cd_kit_p bigint default null, ie_futuro_p text default 'N', nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) FROM PUBLIC;

