CREATE OR REPLACE PROCEDURE cpoe_gerar_sinalv_vital ( NR_ATENDIMENTO_P bigint, QT_ALTURA_CM_P bigint, QT_PESO_P bigint, nm_usuario_p text) AS $body$
BEGIN
	
	if ((NR_ATENDIMENTO_P IS NOT NULL AND NR_ATENDIMENTO_P::text <> '') and
		(QT_ALTURA_CM_P IS NOT NULL AND QT_ALTURA_CM_P::text <> '') and
		(QT_PESO_P IS NOT NULL AND QT_PESO_P::text <> '')) then
		insert into atendimento_sinal_vital(
			nr_sequencia,
			nr_atendimento,
			dt_sinal_vital,
			dt_atualizacao,
			nm_usuario,
			cd_pessoa_fisica,
			ie_pressao,
			qt_peso,
			qt_altura_cm,
			ie_situacao,
			dt_liberacao,
			ie_aum_trab_resp)
		values (
			nextval('atendimento_sinal_vital_seq'),
			nr_atendimento_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			Obter_Pf_Usuario(nm_usuario_p,'C'),
			'D',
			QT_PESO_P,
			QT_ALTURA_CM_P,
			'A',
			clock_timestamp(),
			'N');
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
