CREATE OR REPLACE PROCEDURE cpoe_gerar_vel_inf_medic_md ( qt_dose_p text, qt_tempo_aplicacao_p bigint, qt_hora_aplicacao_p bigint, ie_tipo_dosagem_p text, qt_dosagem_p bigint, qt_vel_infusao_p INOUT bigint, cd_tipo_dosagem_p INOUT bigint) AS $body$
DECLARE


cd_tipo_dosagem_w    bigint;
qt_tempo_infusao_w   double precision;
qt_tempo_aplicacao_w double precision;
qt_vel_infusao_w     double precision;
ie_tipo_dosagem_w    varchar(255) := ie_tipo_dosagem_p;


BEGIN

qt_tempo_aplicacao_w := coalesce(qt_tempo_aplicacao_p, 0) + (coalesce(qt_hora_aplicacao_p, 0) * 60);
if (ie_tipo_dosagem_w IS NOT NULL AND ie_tipo_dosagem_w::text <> '') and (coalesce(qt_tempo_aplicacao_w, 0) > 0) then
	if (upper(ie_tipo_dosagem_w) = 'MLH') then
		qt_tempo_infusao_w := dividir_md(qt_tempo_aplicacao_w, 60); -- Transforma em horas 
		qt_vel_infusao_w   := round((dividir_md(qt_dose_p, qt_tempo_infusao_w))::numeric,2); -- Realizado a divisao ao inves da multiplicacao 
		cd_tipo_dosagem_w  := 327075; -- mlh 
	elsif	((upper(ie_tipo_dosagem_w) = 'GTM') or (upper(ie_tipo_dosagem_w) = 'GPM')) then
		qt_tempo_infusao_w := dividir_md(qt_tempo_aplicacao_w, 60);
		qt_vel_infusao_w   := round((dividir_md(dividir_md(qt_dose_p * 20,qt_tempo_infusao_w),60))::numeric,2);
		cd_tipo_dosagem_w  := 327073; -- gotas min 
	elsif	((upper(ie_tipo_dosagem_w) = 'MGM') or (upper(ie_tipo_dosagem_w) = 'MGPM')) then
		qt_tempo_infusao_w := dividir_md(qt_tempo_aplicacao_w, 60);
		qt_vel_infusao_w   := round((dividir_md(dividir_md(qt_dose_p * 60, qt_tempo_infusao_w), 60))::numeric, 2);
		cd_tipo_dosagem_w  := 327074; --- mcg min 
	end if;
	if (qt_dosagem_p > 0) then
		qt_vel_infusao_w := qt_dosagem_p;
	end if;
end if;

cd_tipo_dosagem_p := cd_tipo_dosagem_w;
qt_vel_infusao_p := qt_vel_infusao_w;

end;
