SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_inserir_recomendacao_fav ( nr_atendimento_p cpoe_recomendacao.nr_atendimento%type, cd_pessoa_fisica_p cpoe_recomendacao.cd_pessoa_fisica%type, nr_sequencia_p cpoe_fav_recomendacao.nr_sequencia%type, nm_usuario_p cpoe_fav_recomendacao.nm_usuario%type, cd_perfil_p bigint, cd_estabelecimento_p bigint, nr_seq_item_gerado_p INOUT bigint, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, ie_oncologia_p text default 'N', nr_seq_conclusao_apae_p bigint default null, ie_futuro_p text default 'N', nr_seq_cpoe_rp_p cpoe_rp.nr_sequencia%type default null, nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) AS $body$
DECLARE


cd_recomendacao_w		cpoe_recomendacao.cd_recomendacao%type;
ds_recomendacao_w		cpoe_recomendacao.ds_recomendacao%type;
cd_intervalo_w			cpoe_recomendacao.cd_intervalo%type;
cd_intervalo_agora_w	cpoe_recomendacao.cd_intervalo%type;
nr_seq_topografia_w		cpoe_recomendacao.nr_seq_topografia%type;
nr_ocorrencia_w			cpoe_recomendacao.nr_ocorrencia%type;
ie_acm_w				cpoe_recomendacao.ie_acm%type;
ie_urgencia_w			cpoe_recomendacao.ie_urgencia%type;
ie_se_necessario_w		cpoe_recomendacao.ie_se_necessario%type;
ie_administracao_w		cpoe_recomendacao.ie_administracao%type;
ie_evento_unico_w		cpoe_recomendacao.ie_evento_unico%type;
ie_duracao_w			cpoe_recomendacao.ie_duracao%type;
dt_inicio_w				cpoe_recomendacao.dt_inicio%type;
hr_prim_horario_w		cpoe_recomendacao.hr_prim_horario%type;
ds_horarios_w			cpoe_recomendacao.ds_horarios%type;
ds_horarios_aux_w		cpoe_recomendacao.ds_horarios%type;
dt_fim_w				cpoe_recomendacao.dt_fim%type;
nr_sequencia_w			cpoe_recomendacao.nr_sequencia%type;

qt_min_intervalo_w		intervalo_prescricao.qt_min_intervalo%type;
ie_dose_unica_cpoe_w	intervalo_prescricao.ie_dose_unica_cpoe%type;
ie_operacao_w			intervalo_prescricao.ie_operacao%type;

ie_prescr_alta_agora_w		varchar(1);

c01 CURSOR FOR
SELECT	cd_recomendacao,
		ds_recomendacao,
		cd_intervalo,
		nr_seq_topografia,
		nr_ocorrencia,
		ie_acm,
		ie_urgencia,
		ie_se_necessario,
		ie_administracao,
		ie_evento_unico,
		ie_duracao
from	cpoe_fav_recomendacao
where	nr_sequencia = nr_sequencia_p
and		nm_usuario = nm_usuario_p;
						

BEGIN

ie_prescr_alta_agora_w := obter_param_usuario(924, 409, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_prescr_alta_agora_w);
open c01;
loop
fetch c01 into	cd_recomendacao_w,
				ds_recomendacao_w,
				cd_intervalo_w,
				nr_seq_topografia_w,
				nr_ocorrencia_w,
				ie_acm_w,
				ie_urgencia_w,
				ie_se_necessario_w,
				ie_administracao_w,
				ie_evento_unico_w,
				ie_duracao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_horarios_w := '';
	hr_prim_horario_w := '';
	dt_inicio_w	:= clock_timestamp();
	
	
	if (coalesce(obter_se_marca_agora(cpoe_obter_setor_atend_prescr(nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p)),'N') = 'S') then
		cd_intervalo_agora_w := cpoe_busca_intervalo_agora(nr_atendimento_p,'R',cd_estabelecimento_p,cd_perfil_p, nm_usuario_p);

		if (cd_intervalo_agora_w IS NOT NULL AND cd_intervalo_agora_w::text <> '') then
			cd_intervalo_w := cd_intervalo_agora_w;
			ie_urgencia_w  := 0;			
		end if;
	
	end if;	
	
	select	max(qt_min_intervalo),
			coalesce(max(ie_dose_unica_cpoe), 'N')
	into STRICT	qt_min_intervalo_w,
			ie_dose_unica_cpoe_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_w;
	
	if (coalesce(ie_dose_unica_cpoe_w, 'N') = 'S') then
		ie_duracao_w := 'P';
		ie_evento_unico_w := 'S';
		ie_urgencia_w := 0;
		ie_se_necessario_w 	:= 'N';
		ie_acm_w			:= 'N';
	end if;
	
	if (coalesce(ie_prescr_alta_agora_w,'N') = 'S') and (coalesce(ie_item_alta_p,'N') = 'S') and (coalesce(ie_dose_unica_cpoe_w, 'N') <> 'S') then	

		ie_urgencia_w := 0;		

		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, null, clock_timestamp(), 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;

		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;

		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);

		dt_inicio_w	:= to_date(to_char(clock_timestamp(),'dd/mm/yyyy ') || hr_prim_horario_w, 'dd/mm/yyyy hh24:mi');

		if (dt_inicio_w < clock_timestamp()) then

			dt_inicio_w := dt_inicio_w + 1;

		end if;
	elsif (coalesce(ie_dose_unica_cpoe_w, 'N') <> 'S') then
		ie_urgencia_w := '';
		SELECT * FROM cpoe_atualizar_periodo_vig_ant(ds_horarios_w, hr_prim_horario_w, dt_inicio_w) INTO STRICT ds_horarios_w, hr_prim_horario_w, dt_inicio_w;
	end if;
	
	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '') and (obter_operacao_intervalo(cd_intervalo_w) = 'D') then
		dt_inicio_w := obter_proxima_dose_medic(dt_inicio_w, cd_intervalo_w, null, 'N');
	end if;
	
	if ((coalesce(ie_administracao_w,'P') = 'P') or (cd_intervalo_agora_w IS NOT NULL AND cd_intervalo_agora_w::text <> '')) then
		if (coalesce(ie_urgencia_w::text, '') = '') then
			dt_inicio_w := trunc(dt_inicio_w,'hh24') + 1/24;
			
			hr_prim_horario_w  := cpoe_obter_primeiro_horario(nr_atendimento_p, cd_intervalo_w, null, null,  null, null);
			
			if (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
				dt_inicio_w	:= to_date(to_char(dt_inicio_w,'dd/mm/yyyy ')  || hr_prim_horario_w, 'dd/mm/yyyy hh24:mi');				
			end if;
			
			if (dt_inicio_w < clock_timestamp()) then
				dt_inicio_w := dt_inicio_w + 1;
			end if;
		elsif (ie_urgencia_w = '0') then
			dt_inicio_w := dt_inicio_w + 1/1440;
		elsif (ie_urgencia_w = '5') then
			dt_inicio_w := dt_inicio_w + 5/1440;
		elsif (ie_urgencia_w = '10') then
			dt_inicio_w := dt_inicio_w + 10/1440;
		elsif (ie_urgencia_w = '15') then
			dt_inicio_w := dt_inicio_w + 15/1440;
		end if;
		
		hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');
			
		nr_ocorrencia_w 	:= 0;
		ds_horarios_w		:= '';
		ds_horarios_aux_w	:= '';
		SELECT * FROM cpoe_calcular_horario_prescr(	nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;

		if (coalesce(ds_horarios_w::text, '') = '') then
			ds_horarios_w	:= hr_prim_horario_w;
			nr_ocorrencia_w	:= 1;
		end if;
	end if;
	
	dt_fim_w := null;
	if (ie_duracao_w = 'P') then
		dt_fim_w := (dt_inicio_w + 1) - 1/1440;
	end if;
	
	if (ie_retrogrado_p = 'S' or ie_futuro_p = 'S') then -- retrograde/backward item
		dt_inicio_w := dt_inicio_p;
		dt_fim_w := (dt_inicio_w + 1) - 1/1440;
		
		select	coalesce(max(qt_min_intervalo),0)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;
		
		ie_duracao_w 		:= 'P';
		nr_ocorrencia_w 	:= 0;
		ie_urgencia_w 		:= null;		
		ds_horarios_w		:= '';
		ds_horarios_aux_w	:= '';
		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
									
		ds_horarios_w		:= ds_horarios_w || ds_horarios_aux_w;
		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);
	end if;
	
	select	nextval('cpoe_recomendacao_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into cpoe_recomendacao(
					nr_sequencia,
					nr_atendimento,
					cd_pessoa_fisica,
					ie_administracao,
					ie_duracao,
					cd_recomendacao,
					ds_recomendacao,
					nr_seq_topografia,
					dt_inicio,
					dt_fim,
					dt_atualizacao,
					dt_atualizacao_nrec,
					hr_prim_horario,
					cd_intervalo,
					ds_horarios,
					nr_ocorrencia,
					ie_acm,
					ie_se_necessario,
					ie_urgencia,
					ie_evento_unico,
					nm_usuario,
					nm_usuario_nrec,
					cd_perfil_ativo,
					cd_funcao_origem,
					nr_seq_transcricao,
					ie_item_alta,
					ie_prescritor_aux,
					cd_medico,
					ie_retrogrado,
					nr_seq_pepo,
					nr_cirurgia,
					nr_cirurgia_patologia,
					nr_seq_agenda,
					ie_oncologia,
					nr_seq_conclusao_apae,
					ie_futuro,
					nr_seq_cpoe_order_unit)
				values (
					nr_sequencia_w,
					nr_atendimento_p,
					cd_pessoa_fisica_p,
					ie_administracao_w,
					ie_duracao_w,
					cd_recomendacao_w,
					ds_recomendacao_w,
					nr_seq_topografia_w,
					dt_inicio_w,
					dt_fim_w,
					clock_timestamp(),
					clock_timestamp(),
					hr_prim_horario_w,
					cd_intervalo_w,
					ds_horarios_w,
					nr_ocorrencia_w,
					ie_acm_w,
					ie_se_necessario_w,
					ie_urgencia_w,
					ie_evento_unico_w,
					nm_usuario_p,
					nm_usuario_p,
					cd_perfil_p,
					2314,
					nr_seq_transcricao_p,
					ie_item_alta_p,
					ie_prescritor_aux_p,
					cd_medico_p,
					ie_retrogrado_p,
					nr_seq_pepo_p,
					nr_cirurgia_p,
					nr_cirurgia_patologia_p,
					nr_seq_agenda_p,
					ie_oncologia_p,
					nr_seq_conclusao_apae_p,
					ie_futuro_p,
					nr_seq_cpoe_order_unit_p);

    nr_seq_item_gerado_p := nr_sequencia_w;
					
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_inserir_recomendacao_fav ( nr_atendimento_p cpoe_recomendacao.nr_atendimento%type, cd_pessoa_fisica_p cpoe_recomendacao.cd_pessoa_fisica%type, nr_sequencia_p cpoe_fav_recomendacao.nr_sequencia%type, nm_usuario_p cpoe_fav_recomendacao.nm_usuario%type, cd_perfil_p bigint, cd_estabelecimento_p bigint, nr_seq_item_gerado_p INOUT bigint, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, ie_oncologia_p text default 'N', nr_seq_conclusao_apae_p bigint default null, ie_futuro_p text default 'N', nr_seq_cpoe_rp_p cpoe_rp.nr_sequencia%type default null, nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) FROM PUBLIC;

