CREATE OR REPLACE PROCEDURE cpoe_inserir_suplementar_ant ( nr_atendimento_p prescr_medica.nr_atendimento%type, nr_atendimento_ant_p prescr_medica.nr_atendimento%type, cd_paciente_p pessoa_fisica.cd_pessoa_fisica%type, nr_prescricao_p prescr_medica.nr_prescricao%type, nr_sequencia_p prescr_material.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_perfil_p bigint, cd_estabelecimento_p bigint, nr_seq_item_gerado_p INOUT bigint, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, nr_seq_conclusao_apae_p bigint default null, ie_futuro_p text default 'N', nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) AS $body$
DECLARE

						
-- Duplicata da procedure CPOE_REP_GERAR_SUPLEMENTO
cd_material_w			prescr_material.cd_material%type;
cd_unidade_medida_w		prescr_material.cd_unidade_medida%type;
qt_dose_w				prescr_material.qt_dose%type;
ds_horarios_w			prescr_material.ds_horarios%type;
ds_horarios_ww			prescr_material.ds_horarios%type;
hr_prim_horario_w		prescr_material.hr_prim_horario%type;
qt_hora_aplicacao_w		prescr_material.qt_hora_aplicacao%type;
qt_vel_infusao_w		prescr_material.qt_vel_infusao%type;
nr_seq_via_acesso_w		prescr_material.nr_seq_via_acesso%type;
cd_intervalo_w			prescr_material.cd_intervalo%type;
ie_acm_w				prescr_material.ie_acm%type;
ie_se_necessario_w		prescr_material.ie_se_necessario%type;
ie_via_aplicacao_w		prescr_material.ie_via_aplicacao%type;
ie_bomba_infusao_w		prescr_material.ie_bomba_infusao%type;
ds_justificativa_w		prescr_material.ds_justificativa%type;
ds_observacao_w			prescr_material.ds_observacao%type;
nr_ocorrencia_w			prescr_material.nr_ocorrencia%type;
nr_seq_material_w		prescr_material.nr_sequencia%type;
ds_diluicao_edit_w		prescr_material.ds_diluicao_edit%type;
cd_perfil_ativo_w		prescr_material.cd_perfil_ativo%type;

ie_administracao_w		cpoe_dieta.ie_administracao%type;
qt_tempo_aplic_w		cpoe_dieta.qt_tempo_aplic%type;
dt_prim_horario_w		cpoe_dieta.dt_inicio%type;
ie_urgencia_w			cpoe_dieta.ie_urgencia%type:='';
ie_duracao_w			cpoe_dieta.ie_duracao%type := 'C';
ds_horarios_aux_w		cpoe_material.ds_horarios%type:='';

qt_min_intervalo_w 		intervalo_prescricao.qt_min_intervalo%type;

ie_prescr_alta_agora_w	varchar(1);
dt_fim_w				timestamp := null;

c01 CURSOR FOR
SELECT	cd_material,
		cd_unidade_medida,
		qt_dose,
		ds_horarios,
		hr_prim_horario,
		qt_hora_aplicacao,
		qt_vel_infusao,
		nr_seq_via_acesso,
		cd_intervalo,
		coalesce(ie_acm,'N'),
		coalesce(ie_se_necessario,'N'),
		ie_via_aplicacao,
		ie_bomba_infusao,
		ds_justificativa,
		ds_observacao,
		nr_ocorrencia,
		nr_sequencia,
		ds_diluicao_edit
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and		nr_sequencia = nr_sequencia_p;


BEGIN

ie_prescr_alta_agora_w := obter_param_usuario(924, 409, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_prescr_alta_agora_w);

open c01;
loop
fetch c01 into	cd_material_w,
				cd_unidade_medida_w,
				qt_dose_w,
				ds_horarios_w,
				hr_prim_horario_w,
				qt_hora_aplicacao_w,
				qt_vel_infusao_w,
				nr_seq_via_acesso_w,
				cd_intervalo_w,
				ie_acm_w,
				ie_se_necessario_w,
				ie_via_aplicacao_w,
				ie_bomba_infusao_w,
				ds_justificativa_w,
				ds_observacao_w,
				nr_ocorrencia_w,
				nr_seq_material_w,
				ds_diluicao_edit_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	if (coalesce(ie_prescr_alta_agora_w,'N') = 'S') and (coalesce(ie_item_alta_p,'N') = 'S')	then
	
		ie_urgencia_w := 0;
		
		select	max(qt_min_intervalo)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, cd_material_w, clock_timestamp(), 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
		
		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;
	
		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);
		dt_prim_horario_w	:= to_date(to_char(clock_timestamp(),'dd/mm/yyyy ') || hr_prim_horario_w, 'dd/mm/yyyy hh24:mi');
	
		if (dt_prim_horario_w < clock_timestamp()) then
			dt_prim_horario_w := dt_prim_horario_w + 1;
		end if;
	else	
		ie_urgencia_w := '';
		SELECT * FROM cpoe_atualizar_periodo_vig_ant(ds_horarios_w, hr_prim_horario_w, dt_prim_horario_w) INTO STRICT ds_horarios_w, hr_prim_horario_w, dt_prim_horario_w;
	end if;
	
	ie_administracao_w := 'P';
	if (ie_acm_w = 'S') then
		ie_administracao_w	:= 'C';
		ds_horarios_w		:= '';
		hr_prim_horario_w	:= '';
	elsif (ie_se_necessario_w = 'S') then
		ie_administracao_w	:= 'N';
		ds_horarios_w		:= '';
		hr_prim_horario_w	:= '';
	end if;
	
	nr_ocorrencia_w	:= coalesce(nr_ocorrencia_w,1);
	
	qt_hora_aplicacao_w	:= coalesce(qt_hora_aplicacao_w, dividir_sem_round(24, nr_ocorrencia_w));
	
	if (qt_hora_aplicacao_w > 24) then
		qt_hora_aplicacao_w	:= 24;
	end if;
	
	if (coalesce(qt_hora_aplicacao_w,0) > 0) then
		qt_tempo_aplic_w := trunc(qt_hora_aplicacao_w) || ':' || lpad(to_char(mod(qt_hora_aplicacao_w * 60,60)), 2, '0');
	else
		qt_tempo_aplic_w := '24:00';
	end if;
	
	if (ie_retrogrado_p = 'S' or ie_futuro_p = 'S') then -- retrograde/backward item
		dt_prim_horario_w := dt_inicio_p;
		dt_fim_w := (dt_prim_horario_w + 1) - 1/1440;
		ie_duracao_w := 'P';
		ie_urgencia_w := null;
		nr_ocorrencia_w := 0;
		ds_horarios_w := '';
		ds_horarios_aux_w := '';
		
		select	max(qt_min_intervalo)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_w;

		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, cd_material_w, dt_prim_horario_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
		
		ds_horarios_w	:= ds_horarios_w || ds_horarios_aux_w;	
		hr_prim_horario_w	:= obter_prim_dshorarios(ds_horarios_w);
	end if;
	
	select	nextval('cpoe_dieta_seq')
	into STRICT	nr_seq_item_gerado_p
	;
	
	insert into cpoe_dieta(
				nr_sequencia,
				nr_atendimento,
				ie_tipo_dieta,
				ie_administracao,
				ie_duracao,
				ie_continuo,
				ie_referencia,
				dt_inicio,
				dt_fim,
				cd_material,
				cd_unidade_medida_dose,
				qt_dose,
				ds_horarios,
				qt_hora_aplicacao,
				qt_tempo_aplic,
				qt_vel_infusao,
				nr_seq_via_acesso,
				hr_prim_horario,
				cd_intervalo,
				ie_urgencia,
				ie_acm,
				ie_se_necessario,
				ie_via_aplicacao,
				ie_bomba_infusao,
				ds_justificativa,
				ds_observacao,
				nr_ocorrencia,
				ds_orientacao,
				nm_usuario,
				nm_usuario_nrec,
				dt_atualizacao,
				dt_atualizacao_nrec,
				cd_perfil_ativo,
				cd_pessoa_fisica,
				cd_funcao_origem,
				nr_seq_transcricao,
				ie_item_alta,
				ie_prescritor_aux,
				cd_medico,
				ie_retrogrado,
				nr_seq_pepo,
				nr_cirurgia,
				nr_cirurgia_patologia,
				nr_seq_agenda,
				nr_seq_conclusao_apae,
				ie_futuro,
				nr_seq_cpoe_order_unit)
			values (
				nr_seq_item_gerado_p,
				nr_atendimento_p,
				'S',
				ie_administracao_w,
				ie_duracao_w,
				'C',
				'I',
				dt_prim_horario_w,
				dt_fim_w,
				cd_material_w,
				cd_unidade_medida_w,
				qt_dose_w,
				ds_horarios_w,
				qt_hora_aplicacao_w,
				qt_tempo_aplic_w,
				qt_vel_infusao_w,
				nr_seq_via_acesso_w,
				hr_prim_horario_w,
				cd_intervalo_w,
				ie_urgencia_w,
				ie_acm_w,
				ie_se_necessario_w,
				ie_via_aplicacao_w,
				ie_bomba_infusao_w,
				ds_justificativa_w,
				ds_observacao_w,
				nr_ocorrencia_w,
				ds_diluicao_edit_w,
				nm_usuario_p,
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp(),
				cd_perfil_p,
				cd_paciente_p,
				2314,
				nr_seq_transcricao_p,
				ie_item_alta_p,
				ie_prescritor_aux_p,
				cd_medico_p,
				ie_retrogrado_p,
				nr_seq_pepo_p,
				nr_cirurgia_p,
				nr_cirurgia_patologia_p,
				nr_seq_agenda_p,
				nr_seq_conclusao_apae_p,
				ie_futuro_p,
				nr_seq_cpoe_order_unit_p);
				
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_inserir_suplementar_ant ( nr_atendimento_p prescr_medica.nr_atendimento%type, nr_atendimento_ant_p prescr_medica.nr_atendimento%type, cd_paciente_p pessoa_fisica.cd_pessoa_fisica%type, nr_prescricao_p prescr_medica.nr_prescricao%type, nr_sequencia_p prescr_material.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_perfil_p bigint, cd_estabelecimento_p bigint, nr_seq_item_gerado_p INOUT bigint, nr_seq_transcricao_p bigint default null, ie_item_alta_p text default 'N', ie_prescritor_aux_p text default 'N', cd_medico_p bigint default null, ie_retrogrado_p text default 'N', dt_inicio_p timestamp default null, nr_seq_pepo_p bigint default null, nr_cirurgia_p bigint default null, nr_cirurgia_patologia_p bigint default null, nr_seq_agenda_p bigint default null, nr_seq_conclusao_apae_p bigint default null, ie_futuro_p text default 'N', nr_seq_cpoe_order_unit_p cpoe_order_unit.nr_sequencia%type default null) FROM PUBLIC;

