SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_insert_dieta_enteral ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_material_p bigint, qt_dose_p bigint, cd_unidade_medida_dose_p text, ie_via_aplicacao_p text, cd_intervalo_p text, hr_prim_horario_p text, ds_horarios_p text, ie_se_necessario_p text, ie_acm_p text, nr_etapas_p bigint, ds_observacao_p text, ie_bomba_infusao_p text, ie_aplic_bolus_p text, nr_seq_via_acesso_p bigint, qt_vel_infusao_p bigint, qt_tempo_aplic_p bigint, ds_justificativa_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_origem_inf_p text, ie_urgencia_p text, ie_regra_disp_p text default null) AS $body$
DECLARE

nr_seq_prescricao_w		prescr_material.nr_sequencia%type;
nr_agrupamento_w		prescr_material.nr_agrupamento%type;
nr_ocorrencia_w			prescr_material.nr_ocorrencia%type;
qt_unitaria_w			prescr_material.qt_unitaria%type;
qt_conversao_dose_w		prescr_material.qt_conversao_dose%type;
qt_material_w			prescr_material.qt_material%type;
qt_total_dispensar_w	prescr_material.qt_total_dispensar%type;
ie_regra_disp_w			prescr_material.ie_regra_disp%type;
qt_solucao_w			prescr_material.qt_solucao%type;
nr_atendimento_w		prescr_medica.nr_atendimento%type;

cd_unidade_medida_w		unidade_medida.cd_unidade_medida%type;

ds_erro_w				varchar(4000);
ds_erro_ex_w			varchar(2000);	


BEGIN

select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_seq_prescricao_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p;

select	coalesce(max(nr_agrupamento),0) + 1
into STRICT	nr_agrupamento_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p;

qt_conversao_dose_w := coalesce(obter_conversao_unid_med(cd_material_p, cd_unidade_medida_dose_p),1);

if (qt_conversao_dose_w <= 0) then
	qt_conversao_dose_w := 1;
end if;

ie_regra_disp_w		:= 'S';
cd_unidade_medida_w	:= obter_dados_material_estab(cd_material_p,cd_estabelecimento_p,'UMS');

nr_ocorrencia_w := coalesce(nr_etapas_p,1);

qt_unitaria_w := dividir( qt_dose_p, qt_conversao_dose_w);
qt_total_dispensar_w := qt_unitaria_w * nr_ocorrencia_w;

qt_material_w	:= qt_unitaria_w;

SELECT * FROM obter_quant_dispensar(	cd_estabelecimento_p, cd_material_p, nr_prescricao_p, nr_seq_prescricao_w, cd_intervalo_p, ie_via_aplicacao_p, qt_unitaria_w, 0, nr_ocorrencia_w, null, ie_origem_inf_p, cd_unidade_medida_dose_p, 0, qt_material_w, qt_total_dispensar_w, ie_regra_disp_w, ds_erro_w, ie_se_necessario_p, ie_acm_p ) INTO STRICT qt_material_w, qt_total_dispensar_w, ie_regra_disp_w, ds_erro_w;
						
if (upper(cd_unidade_medida_dose_p) = upper(obter_unid_med_usua('ML'))) then
	qt_solucao_w	:= qt_dose_p;
else
	qt_solucao_w	:= Obter_conversao_ml( cd_material_p, qt_dose_p, cd_unidade_medida_dose_p );
end if;

if (ie_regra_disp_p IS NOT NULL AND ie_regra_disp_p::text <> '') then
	ie_regra_disp_w := ie_regra_disp_p;
end if;

insert into prescr_material(
			nr_prescricao,
			nr_sequencia,
			ie_origem_inf,
			cd_unidade_medida,
			qt_unitaria,
			qt_material,
			dt_atualizacao,
			nm_usuario,
			cd_material,
			qt_dose,
			cd_unidade_medida_dose,
			qt_solucao,
			ie_agrupador,
			nr_sequencia_diluicao,
			nr_agrupamento,
			qt_conversao_dose,
			ie_medicacao_paciente,
			ie_suspenso,
			ie_via_aplicacao,
			ie_bomba_infusao,
			ie_aplic_bolus,
			cd_perfil_ativo,
			cd_intervalo,
			ds_horarios,
			ds_observacao,
			nr_seq_via_acesso,
			qt_vel_infusao,
			ie_se_necessario,
			ie_acm,
			nr_seq_dieta_cpoe,
			qt_hora_aplicacao,
			ie_urgencia,
			ds_justificativa,
			hr_prim_horario,
			nr_ocorrencia,
			qt_total_dispensar,
			ie_regra_disp,
			ie_utiliza_kit,
			ie_aplic_lenta,
			ie_uso_antimicrobiano,
			ie_sem_aprazamento,
			ie_supera_limite_uso,
			ie_dose_espec_agora,
			ie_kit_gerado,
			ie_permite_substituir,
			ie_permite_alterar,
			ie_alterar_horario,
			ie_calc_automatico,
			qt_volume
		) values (
			nr_prescricao_p,
			nr_seq_prescricao_w,
			ie_origem_inf_p,
			cd_unidade_medida_w,
			qt_unitaria_w,
			qt_material_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_material_p,
			qt_dose_p,
			cd_unidade_medida_dose_p,
			qt_solucao_w,
			8,
			null,
			nr_agrupamento_w,
			qt_conversao_dose_w,
			'N',
			'N',
			ie_via_aplicacao_p,
			ie_bomba_infusao_p,
			ie_aplic_bolus_p,
			cd_perfil_p,
			cd_intervalo_p,
			ds_horarios_p,
			ds_observacao_p,
			nr_seq_via_acesso_p,
			qt_vel_infusao_p,
			ie_se_necessario_p,
			ie_acm_p,
			nr_sequencia_p,
			qt_tempo_aplic_p,
			CASE WHEN coalesce(ie_urgencia_p::text, '') = '' THEN 'N'  ELSE 'S' END ,
			ds_justificativa_p,
			hr_prim_horario_p,
			nr_ocorrencia_w,
			qt_total_dispensar_w,
			coalesce(ie_regra_disp_w,'S'),
			'N',
			'N',
			'N',
			'N',
			'N',
			'N',
			'N',
			'S',
			'S',
			'S',
			'S',
			0
	);
				
commit;

exception when others then
	if (coalesce(nr_prescricao_p,0) > 0) then
		select max(nr_atendimento)
		into STRICT nr_atendimento_w
		from prescr_medica
		where nr_prescricao = nr_prescricao_p;
	end if;
	

	CALL gravar_log_cpoe(substr('CPOE_INSERT_DIETA_ENTERAL EXCEPTION:'|| substr(to_char(sqlerrm),1,2000)
		||'nr_sequencia_p: '||nr_sequencia_p||' nr_prescricao_p :'||nr_prescricao_p||'cd_material_p:'||cd_material_p||'qt_dose_p:'||qt_dose_p
		||'cd_unidade_medida_dose_p:'||cd_unidade_medida_dose_p||'ie_via_aplicacao_p:'||ie_via_aplicacao_p||'cd_intervalo_p:'||cd_intervalo_p
		||'hr_prim_horario_p:'|| hr_prim_horario_p||'ds_horarios_p : '||ds_horarios_p||'ie_se_necessario_p:'||ie_se_necessario_p
		||'ie_se_necessario_p:'|| ie_se_necessario_p||'ie_acm_p:'||ie_acm_p||'nr_etapas_p:'||nr_etapas_p
		||'ds_observacao_p:'||ds_observacao_p||'ie_bomba_infusao_p:'||ie_bomba_infusao_p||'ie_aplic_bolus_p:'||ie_aplic_bolus_p
		||'nr_seq_via_acesso_p:'||nr_seq_via_acesso_p||'qt_vel_infusao_p:'||qt_vel_infusao_p||'qt_tempo_aplic_p:'||qt_tempo_aplic_p
		||'ds_justificativa_p:'||ds_justificativa_p||'cd_estabelecimento_p:'||cd_estabelecimento_p||'cd_perfil_p :'|| cd_perfil_p
		||'nm_usuario_p:'|| nm_usuario_p||'ie_origem_inf_p:'||ie_origem_inf_p,1,2000),
		nr_atendimento_w, 'N', nr_sequencia_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
