				ie_prod_adicional,
				ie_somar_volume,
				ie_supera_limite_uso)
			values (
				nr_seq_nut_pac_elem_mat_w,
				nr_seq_nut_pac_w,
				cd_material_w,
				dt_atualizacao_w,
				nm_usuario_w,
				dt_atualizacao_nrec_w,
				nm_usuario_nrec_w,
				nr_seq_elem_mat_w,
				qt_volume_w,
				qt_vol_1_fase_w,
				qt_vol_2_fase_w,
				qt_vol_3_fase_w,
				qt_vol_4_fase_w,
				qt_vol_cor_w,
				qt_protocolo_w,
				qt_dose_w,
				cd_unidade_medida_w,
				ie_prod_adicional_w,
				ie_somar_volume_w,
				ie_supera_limite_uso_w);
		end;
	end loop;
	close c03;

	select	count(distinct(trunc(dt_prescricao))) + 1 qt_dias_npt
	into STRICT	qt_dia_npt_cpoe_w
	from	prescr_medica b,
			nut_pac a
	where	a.nr_prescricao = b.nr_prescricao
	and		trunc(b.dt_prescricao) <> trunc(clock_timestamp())
	and		b.nr_atendimento = nr_atendimento_p;

	update	cpoe_dieta
	set		qt_dia_npt = qt_dia_npt_cpoe_w
	where	nr_sequencia = nr_seq_cpoe_dieta_p;

	end;
end loop;
close c01;

commit;
exception when others then
	CALL gravar_log_cpoe(substr('CPOE_INSERT_DIETA_SUPLEM EXCEPTION:'|| substr(to_char(sqlerrm),1,2000)
		||'nr_seq_cpoe_dieta_p:'||nr_seq_cpoe_dieta_p||' nr_atendimento_p :'||nr_atendimento_p||'nr_prescricao_p:'||nr_prescricao_p
		||'cd_estabelecimento_p:'||cd_estabelecimento_p||'cd_perfil_p:'||cd_perfil_p||'ie_origem_inf_p:'||ie_origem_inf_p,1,2000),
		nr_atendimento_p, 'N', nr_seq_cpoe_dieta_p);
end;
$body$
