CREATE OR REPLACE PROCEDURE cpoe_insert_prot_dieta_enteral ( nr_atendimento_p cpoe_dieta.nr_atendimento%type, cd_protocolo_p protocolo_medic_material.cd_protocolo%type, nr_seq_protocolo_p protocolo_medic_material.nr_sequencia%type, nr_seq_mat_prot_p protocolo_medic_material.nr_seq_material%type, nm_usuario_p cpoe_dieta.nm_usuario%type, cd_estabelecimento_p bigint, cd_perfil_p bigint, nr_seq_dieta_enteral_p INOUT cpoe_dieta.nr_sequencia%type) AS $body$
DECLARE


cd_material_w			protocolo_medic_material.cd_material%type;
cd_unidade_medida_w		protocolo_medic_material.cd_unidade_medida%type;
qt_dose_w				protocolo_medic_material.qt_dose%type;
ie_bomba_infusao_w		protocolo_medic_material.ie_bomba_infusao%type;
hr_prim_horario_w		protocolo_medic_material.hr_prim_horario%type;
qt_vel_infusao_w		protocolo_medic_material.qt_vel_infusao%type;
cd_intervalo_w			protocolo_medic_material.cd_intervalo%type;

nr_sequencia_w			cpoe_dieta.nr_sequencia%type;
dt_inicio_w				cpoe_dieta.dt_inicio%type;
ds_horarios_w			cpoe_dieta.ds_horarios%type;
ds_horarios_ww			cpoe_dieta.ds_horarios%type;

nr_ocorrencia_w			intervalo_prescricao.nr_etapas%type;
qt_min_intervalo_w		intervalo_prescricao.qt_min_intervalo%type;

c01 CURSOR FOR
SELECT	cd_material,
		cd_unidade_medida,
		qt_dose,
		ie_bomba_infusao ,
		hr_prim_horario,
		qt_vel_infusao,
		cd_intervalo
from 	protocolo_medic_material
where	cd_protocolo	 			= cd_protocolo_p
and		nr_sequencia				= nr_seq_protocolo_p
and		nr_seq_material				= nr_seq_mat_prot_p
order by
		nr_seq_material;


BEGIN

open c01;
loop
fetch c01 into	cd_material_w,
				cd_unidade_medida_w,
				qt_dose_w,
				ie_bomba_infusao_w,
				hr_prim_horario_w,
				qt_vel_infusao_w,
				cd_intervalo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
		dt_inicio_w := to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi:ss');
		if (dt_inicio_w <= clock_timestamp()) then
			dt_inicio_w := dt_inicio_w +1;
		end if;
	else
		dt_inicio_w := trunc(clock_timestamp(),'hh');
		if (dt_inicio_w <= clock_timestamp()) then
			dt_inicio_w := dt_inicio_w +1/24;
		end if;
		hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');
	end if;

	if (cd_intervalo_w IS NOT NULL AND cd_intervalo_w::text <> '') then
		select	coalesce(max(qt_min_intervalo),0)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_w;
		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, cd_material_w, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_ww, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_ww;
		ds_horarios_w := ds_horarios_w||ds_horarios_ww;
	else
		ds_horarios_w := hr_prim_horario_w;
	end if;

	select	nextval('cpoe_dieta_seq')
	into STRICT	nr_sequencia_w
	;

	insert into cpoe_dieta(
			nr_sequencia,
			nr_atendimento,
			ie_tipo_dieta,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			ie_administracao,
			ie_referencia,
			ds_horarios,
			hr_prim_horario,
			dt_inicio,
			cd_material,
			cd_intervalo,
			qt_dose,
			ie_bomba_infusao,
			qt_vel_infusao,
			cd_unidade_medida_dose)
		values (
			nr_sequencia_w,
			nr_atendimento_p,
			'E',
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			'P',
			'I',
			ds_horarios_w,
			hr_prim_horario_w,
			dt_inicio_w,
			cd_material_w,
			cd_intervalo_w,
			qt_dose_w,
			ie_bomba_infusao_w,
			qt_vel_infusao_w,
			cd_unidade_medida_w);

	nr_seq_dieta_enteral_p := nr_sequencia_w;
	end;
end loop;
close c01;

commit;

end;
$body$
