CREATE OR REPLACE PROCEDURE cpoe_insert_prot_dieta_oral ( nr_atendimento_p cpoe_dieta.nr_atendimento%type, cd_protocolo_p protocolo_medic_dieta.cd_protocolo%type, nr_sequencia_prot_p protocolo_medic_dieta.nr_sequencia%type, nr_seq_dieta_prot_p protocolo_medic_dieta.nr_seq_dieta%type, nm_usuario_p cpoe_dieta.nm_usuario%type, cd_estabelecimento_p bigint, cd_perfil_p bigint, nr_seq_dieta_p INOUT cpoe_dieta.nr_sequencia%type) AS $body$
DECLARE


cd_dieta_w				protocolo_medic_dieta.cd_dieta%type;
ie_destino_dieta_w		protocolo_medic_dieta.ie_destino_dieta%type;
ie_refeicao_w			protocolo_medic_dieta.ie_refeicao%type;
ds_horarios_w			protocolo_medic_dieta.ds_horarios%type;
ds_horarios_ww			protocolo_medic_dieta.ds_horarios%type;
ds_observacao_w			protocolo_medic_dieta.ds_observacao%type;
cd_intervalo_w			protocolo_medic_dieta.cd_intervalo%type;

dt_inicio_w				cpoe_dieta.dt_inicio%type;
hr_prim_horario_w		cpoe_dieta.hr_prim_horario%type;
nr_sequencia_w			cpoe_dieta.nr_sequencia%type;

qt_min_intervalo_w		intervalo_prescricao.qt_min_intervalo%type;
nr_ocorrencia_w			intervalo_prescricao.nr_etapas%type;

c01 CURSOR FOR
SELECT	cd_dieta,
		ie_destino_dieta,
		ie_refeicao,
		ds_horarios,
		ds_observacao,
		cd_intervalo
from	protocolo_medic_dieta
where	nr_sequencia = nr_sequencia_prot_p
and		cd_protocolo = cd_protocolo_p
and		nr_seq_dieta = nr_seq_dieta_prot_p;


BEGIN
open c01;
loop
fetch c01 into	cd_dieta_w,
				ie_destino_dieta_w,
				ie_refeicao_w,
				ds_horarios_w,
				ds_observacao_w,
				cd_intervalo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	dt_inicio_w := trunc(clock_timestamp(),'hh');


	if (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then
		ds_horarios_w := reordenar_horarios_prescr(dt_inicio_w, ds_horarios_w);
		if (position(' ' in ds_horarios_w) > 0) then
			if (position(':' in ds_horarios_w) > 0) then
				hr_prim_horario_w := substr(ds_horarios_w,position(' ' in ds_horarios_w) - 1);
			else
				hr_prim_horario_w := substr(ds_horarios_w,position(' ' in ds_horarios_w) - 1)||':00';
			end if;
		else
			if (position(':' in ds_horarios_w) > 0) then
				hr_prim_horario_w := ds_horarios_w;
			else
				hr_prim_horario_w := ds_horarios_w||':00';
			end if;
		end if;
		dt_inicio_w := to_date(to_char(dt_inicio_w,'dd/mm/yyyy')||hr_prim_horario_w,'dd/mm/yyyy hh24:mi:ss');
		if (dt_inicio_w <= clock_timestamp()) then
			dt_inicio_w := dt_inicio_w + 1;
		end if;
	else
		if (dt_inicio_w <= clock_timestamp()) then
			dt_inicio_w := dt_inicio_w +1/24;
		end if;
		hr_prim_horario_w := to_char(dt_inicio_w,'hh24:mi');

		select	coalesce(max(qt_min_intervalo),0)
		into STRICT	qt_min_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_w;

		nr_ocorrencia_w := 0;
		ds_horarios_w	:= '';
		ds_horarios_ww	:= '';
		SELECT * FROM cpoe_calcular_horario_prescr(nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, 0, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_ww, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_ww;
		ds_horarios_w	:= ds_horarios_w || ds_horarios_ww;

		if (coalesce(ds_horarios_w::text, '') = '') then
			ds_horarios_w := hr_prim_horario_w;
		end if;
	end if;

	select	nextval('cpoe_dieta_seq')
	into STRICT	nr_sequencia_w
	;

	insert into cpoe_dieta(
			nr_sequencia,
			nr_atendimento,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			ie_tipo_dieta,
			dt_inicio,
			hr_prim_horario,
			cd_intervalo,
			ds_horarios,
			cd_dieta,
			ds_observacao,
			ie_administracao)
		values (
			nr_sequencia_w,
			nr_atendimento_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			'O',
			dt_inicio_w,
			hr_prim_horario_w,
			cd_intervalo_w,
			ds_horarios_w,
			cd_dieta_w,
			ds_observacao_w,
			'P');
	nr_seq_dieta_p := nr_sequencia_w;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
