CREATE OR REPLACE PROCEDURE cpoe_regra_obriga_campos_proc ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_proc_interno_p cpoe_procedimento.nr_seq_proc_interno%type, cd_intervalo_p cpoe_procedimento.cd_intervalo%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type, ds_campo_obriga_p INOUT text) AS $body$
DECLARE

											
ie_campo_obriga_w 			regra_indic_clinica_obrig.ie_campo_obriga%type;
cd_convenio_w				convenio.cd_convenio%type;
cd_tipo_convenio_w			convenio.ie_tipo_convenio%type;
ie_origem_proced_w			proc_interno.ie_origem_proced%type;
cd_procedimento_w			proc_interno.cd_procedimento%type;
cd_especialidade_proced_w	grupo_proc.cd_especialidade%type;
cd_setor_atendimento_w		setor_atendimento.cd_setor_atendimento%type;
nr_seq_exame_w				proc_interno.nr_seq_exame_lab%type;
cd_area_procedimento_w		regra_indic_clinica_obrig.cd_area_procedimento%type;
cd_grupo_procedimento_w		procedimento.cd_grupo_proc%type;
nr_seq_subgrupo_w			regra_indic_clinica_obrig.nr_seq_subgrupo%type;
nr_seq_grupo_exame_w		regra_indic_clinica_obrig.nr_seq_grupo_lab%type;
cd_tipo_procedimento_w		procedimento.cd_tipo_procedimento%type;
ie_count_contraste_w		bigint;

C01 CURSOR FOR
	SELECT	ie_campo_obriga
	from	regra_indic_clinica_obrig
	where	coalesce(nr_seq_exame_interno,nr_seq_proc_interno_p, 0)	= coalesce(nr_seq_proc_interno_p, 0)
	and		coalesce(cd_especialidade,cd_especialidade_proced_w, 0)	= coalesce(cd_especialidade_proced_w, 0)
	and		coalesce(cd_procedimento,cd_procedimento_w, 0) = coalesce(cd_procedimento_w, 0)
	and		coalesce(cd_intervalo,coalesce(cd_intervalo_p,0)) = coalesce(cd_intervalo_p,0)
	and		coalesce(ie_origem_proced,ie_origem_proced_w, 0) = coalesce(ie_origem_proced_w, 0)
	and		coalesce(cd_perfil,cd_perfil_p,0) = coalesce(cd_perfil_p,0)
	and		coalesce(cd_setor_prescricao,cd_setor_atendimento_w, 0)	= coalesce(cd_setor_atendimento_w, 0)
	and		coalesce(ie_tipo_convenio, cd_tipo_convenio_w, 0) = coalesce(cd_tipo_convenio_w, 0)
	and		coalesce(nr_seq_exame_lab,nr_seq_exame_w,0)		= coalesce(nr_seq_exame_w,0)
	and		coalesce(cd_area_procedimento,cd_area_procedimento_w,0)	= coalesce(cd_area_procedimento_w,0)
	and		coalesce(cd_grupo_proc,cd_grupo_procedimento_w,0)	= coalesce(cd_grupo_procedimento_w,0)
	and		coalesce(cd_tipo_procedimento, cd_tipo_procedimento_w,0) = coalesce(cd_tipo_procedimento_w,0)
	and		coalesce(nr_seq_grupo_lab,nr_seq_grupo_exame_w,0)	= coalesce(nr_seq_grupo_exame_w,0)
	and		coalesce(nr_seq_subgrupo,nr_seq_subgrupo_w)			= nr_seq_subgrupo_w
	and		coalesce(cd_convenio, cd_convenio_w) = cd_convenio_w
	and		cd_estabelecimento = cd_estabelecimento_p;
	
	--and		((coalesce(ie_autorizacao,'N') = 'N') or (ie_regra_plano_w = 5))
	

BEGIN

cd_setor_atendimento_w := obter_setor_atendimento(nr_atendimento_p);
cd_convenio_w		:= obter_convenio_atendimento(nr_atendimento_p);
cd_tipo_convenio_w	:= Obter_Tipo_Convenio(cd_convenio_w);

select 	max(a.ie_origem_proced),
		max(a.cd_procedimento),
		obter_especialidade_proced(max(a.cd_procedimento), max(a.ie_origem_proced)),
		max(a.nr_seq_exame_lab),
		obter_area_procedimento(max(a.cd_procedimento), max(a.ie_origem_proced)),
		max(b.cd_grupo_proc),
		max(b.cd_tipo_procedimento),
		coalesce(substr(sus_obter_seq_estrut_proc(sus_obter_estrut_proc(max(a.cd_procedimento),max(a.ie_origem_proced),'C','S'),'S'),1,10),0),
		obter_grupo_exame_lab(max(a.nr_seq_exame_lab))
into STRICT	ie_origem_proced_w,
		cd_procedimento_w,
		cd_especialidade_proced_w,
		nr_seq_exame_w,
		cd_area_procedimento_w,
		cd_grupo_procedimento_w,
		cd_tipo_procedimento_w,
		nr_seq_subgrupo_w,
		nr_seq_grupo_exame_w
from	proc_interno a,
		procedimento b
where	a.nr_sequencia = nr_seq_proc_interno_p
and		a.cd_procedimento = b.cd_procedimento
and 	coalesce(b.IE_SITUACAO,'I') = 'A';

select 	count(nr_sequencia)
into STRICT 	ie_count_contraste_w
from 	proc_interno_contraste
where 	nr_seq_proc_interno = nr_seq_proc_interno_p
and 	ie_situacao = 'A';

open C01;
loop
fetch C01 into	
	ie_campo_obriga_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
		if ((ie_campo_obriga_w = '7' and ie_count_contraste_w > 0) or (ie_campo_obriga_w <> '7') ) then
			ds_campo_obriga_p := ie_campo_obriga_w ||';'|| ds_campo_obriga_p;
		end if;
	end;
end loop;
close C01;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_regra_obriga_campos_proc ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_proc_interno_p cpoe_procedimento.nr_seq_proc_interno%type, cd_intervalo_p cpoe_procedimento.cd_intervalo%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type, ds_campo_obriga_p INOUT text) FROM PUBLIC;

