CREATE OR REPLACE PROCEDURE cpoe_replace_material ( cd_material_p bigint, nr_prescricao_p bigint, nr_seq_mat_cpoe_p bigint, ie_lote_atendido_p text, ie_liberar_p text, nm_usuario_p text, ds_itens_liberar_p INOUT text, nr_prescricao_out_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w		prescr_medica.cd_estabelecimento%type;
cd_setor_atendimento_w		prescr_medica.cd_setor_atendimento%type;
cd_perfil_w					prescr_medica.cd_perfil_ativo%type;
nm_usuario_w				prescr_medica.nm_usuario%type;
cd_pessoa_fisica_w			prescr_medica.cd_pessoa_fisica%type;
ie_motivo_prescricao_w		prescr_medica.ie_motivo_prescricao%type;
cd_medico_w					prescr_medica.cd_medico%type;
ie_adep_w					prescr_medica.ie_adep%type;
nr_atendimento_w			prescr_medica.nr_atendimento%type;
nr_prescricao_out_w		    prescr_medica.nr_prescricao%type;
cd_prescritor_w				prescr_medica.cd_prescritor%type;
cd_departamento_w			cpoe_material.cd_departamento%type;
nr_seq_mat_cpoe_novo_w		cpoe_material.nr_sequencia%type;
dt_fim_w					cpoe_material.dt_fim%type;

ie_opcao_suspender_w      	varchar(50);
ds_erro_w			        varchar(4000);
ds_inconsist_lib_out_w		varchar(4000);
ds_itens_liberados_w		varchar(4000);
ds_lista_proc_susp_w		varchar(4000);
ds_prescr_geradas_full_w	varchar(4000);
ie_inconsistencia_out_w		varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '')  then

	select	cd_estabelecimento,
			cd_setor_atendimento,
			cd_perfil_ativo,
			nm_usuario_original,
			cd_pessoa_fisica,
			ie_motivo_prescricao,
			cd_medico,
			ie_adep,
			nr_atendimento,
			cd_prescritor
	into STRICT	cd_estabelecimento_w,
			cd_setor_atendimento_w,
			cd_perfil_w,
			nm_usuario_w,
			cd_pessoa_fisica_w,
			ie_motivo_prescricao_w,
			cd_medico_w,
			ie_adep_w,
			nr_atendimento_w,
			cd_prescritor_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_p;
	
	if (nr_seq_mat_cpoe_p IS NOT NULL AND nr_seq_mat_cpoe_p::text <> '') then
		select 	max(dt_fim)
		into STRICT	dt_fim_w
		from	cpoe_material
		where	nr_sequencia  = nr_seq_mat_cpoe_p;
		
		if (dt_fim_w < clock_timestamp()) then
			dt_fim_w := null;
		end if;
		
		nr_seq_mat_cpoe_novo_w := cpoe_duplicar_registro('CPOE_MATERIAL', nm_usuario_p, nr_seq_mat_cpoe_p, nr_seq_mat_cpoe_novo_w);
	end if;
	
	if (nr_seq_mat_cpoe_novo_w IS NOT NULL AND nr_seq_mat_cpoe_novo_w::text <> '') then
	
		if (ie_lote_atendido_p = 'N') then
		
			ie_opcao_suspender_w :=  nr_seq_mat_cpoe_p || ';' || 'M' || ';' || 'S' || ', ';
			ds_lista_proc_susp_w := cpoe_suspender_item_prescr('[' || ie_opcao_suspender_w || ']', nr_atendimento_w, nm_usuario_p, null, null, 'N', ds_lista_proc_susp_w, 'S', 'S');
			
			update	cpoe_material
			set		cd_material 			= cd_material_p,
					nr_seq_cpoe_anterior 	= nr_seq_mat_cpoe_p,
					dt_fim					= dt_fim_w,
					dt_lib_suspensao 		 = NULL
			where	nr_sequencia 			= nr_seq_mat_cpoe_novo_w;
			
		end if;
		
		select	max(cd_departamento)
		into STRICT	cd_departamento_w
		from	cpoe_material
		where	nr_sequencia = nr_seq_mat_cpoe_novo_w;

		ds_itens_liberar_p  :=	ds_itens_liberar_p || 'M' || ',' || nr_seq_mat_cpoe_novo_w || ';';
	end if;
	
	if (ie_liberar_p = 'S') then
		begin
			nr_prescricao_out_w := cpoe_gerar_prescricao_rep(	nr_atendimento_W, trunc(clock_timestamp(),'mi') + 1/1440, cd_estabelecimento_W, cd_perfil_W, cd_setor_atendimento_W, nm_usuario_p, ds_itens_liberar_p, cd_pessoa_fisica_W, 'N', 'N', 'N', null, ie_motivo_prescricao_w, cd_medico_w, cd_prescritor_w, 'N', null, null, null, null, null, null, 'N', ie_adep_w);

			if (coalesce(ie_inconsistencia_out_w,'A') <> 'N') and (nr_prescricao_out_w IS NOT NULL AND nr_prescricao_out_w::text <> '') then
		
				ds_erro_p					=>	ds_erro_w := Liberar_prescricao(	nr_prescricao_p				=>	nr_prescricao_out_w, nr_atendimento_p			=>	nr_atendimento_w, ie_tipo_pessoa_p			=>	'S', cd_perfil_p					=>	cd_perfil_w, nm_usuario_p				=>	nm_usuario_w, ie_prescricao_p				=>	'S', ds_erro_p					=>	ds_erro_w, ie_gera_hor_copia_cpoe_p	=>	'N');

			end if;
		exception when others then
			rollback;
				CALL gravar_log_cpoe(substr('CPOE_REPLACE_MATERIAL ERRO LIBERACAO: '||substr(to_char(sqlerrm),1,2000)
				||' nr_atendimento_w: '||nr_atendimento_w
				||' nr_prescricao_w : '|| nr_prescricao_p
				||' cd_estabelecimento_w : '|| cd_estabelecimento_w
				||' cd_setor_atendimento_w : '|| cd_setor_atendimento_w
				||' cd_perfil_w : '|| cd_perfil_w
				||' cd_pessoa_fisica_w : '|| cd_pessoa_fisica_w
				||' ie_motivo_prescricao_w : '|| ie_motivo_prescricao_w
				||' cd_medico_w : '|| cd_medico_w
				||' ie_adep_w : '|| ie_adep_w
				||' nr_prescricao_out_p: '|| nr_prescricao_out_w
				||' ds_erro_w : '|| ds_erro_w		 	        
				||' ds_itens_liberar_p : '|| ds_itens_liberar_p			
				||' ds_inconsist_lib_out_w : '|| ds_inconsist_lib_out_w		
				||' ds_itens_liberados_w : '|| ds_itens_liberados_w	
				||' ds_lista_proc_susp_w : '|| ds_lista_proc_susp_w		
				||' ds_prescr_geradas_full_w : '|| ds_prescr_geradas_full_w	
				||' ie_inconsistencia_out_w : '|| ie_inconsistencia_out_w
				||' nm_usuario_p :'||nm_usuario_p
				||' ie_liberar_p :' || ie_liberar_p, 1, 2000),
				nr_atendimento_w, 'M', nr_seq_mat_cpoe_novo_w);
		commit;
		end;
	end if;	
	commit;
end if;

nr_prescricao_out_p := nr_prescricao_out_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_replace_material ( cd_material_p bigint, nr_prescricao_p bigint, nr_seq_mat_cpoe_p bigint, ie_lote_atendido_p text, ie_liberar_p text, nm_usuario_p text, ds_itens_liberar_p INOUT text, nr_prescricao_out_p INOUT text) FROM PUBLIC;

