SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_rep_gerar_dieta_enteral ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, dt_inicio_prescr_p timestamp, dt_validade_prescr_p timestamp, dt_liberacao_p timestamp, nm_usuario_p usuario.nm_usuario%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, cd_funcao_origem_p funcao.cd_funcao%type, cd_setor_atendimento_p prescr_medica.cd_setor_atendimento%type default null) AS $body$
DECLARE

					
cd_material_w			prescr_material.cd_material%type;
cd_unidade_medida_w		prescr_material.cd_unidade_medida%type;
qt_dose_w				prescr_material.qt_dose%type;
ds_horarios_w			prescr_material.ds_horarios%type;
hr_prim_horario_w		prescr_material.hr_prim_horario%type;
qt_hora_aplicacao_w		prescr_material.qt_hora_aplicacao%type;
qt_vel_infusao_w		prescr_material.qt_vel_infusao%type;
nr_seq_via_acesso_w		prescr_material.nr_seq_via_acesso%type;
cd_intervalo_w			prescr_material.cd_intervalo%type;
ie_urgencia_w			prescr_material.ie_urgencia%type;
ie_acm_w				prescr_material.ie_acm%type;
ie_se_necessario_w		prescr_material.ie_se_necessario%type;
ie_via_aplicacao_w		prescr_material.ie_via_aplicacao%type;
ie_bomba_infusao_w		prescr_material.ie_bomba_infusao%type;
ds_justificativa_w		prescr_material.ds_justificativa%type;
ds_observacao_w			prescr_material.ds_observacao%type;
nr_ocorrencia_w			prescr_material.nr_ocorrencia%type;
nr_seq_material_w		prescr_material.nr_sequencia%type;
ds_diluicao_edit_w		prescr_material.ds_diluicao_edit%type;
cd_perfil_ativo_w		prescr_material.cd_perfil_ativo%type;

nr_sequencia_w			cpoe_dieta.nr_sequencia%type;
ie_administracao_w		cpoe_dieta.ie_administracao%type;
qt_tempo_aplic_w		cpoe_dieta.qt_tempo_aplic%type;
dt_prim_horario_w		cpoe_dieta.dt_inicio%type;

ie_retrogrado_w			prescr_medica.ie_prescr_emergencia%type;
dt_liberacao_enf_w		timestamp;
nm_usuario_lib_enf_w	prescr_medica.nm_usuario_lib_enf%type;
cd_medico_w				prescr_medica.cd_medico%type;

c01 CURSOR FOR
SELECT	cd_material,
		cd_unidade_medida,
		qt_dose,
		ds_horarios,
		hr_prim_horario,
		qt_hora_aplicacao,
		qt_vel_infusao,
		nr_seq_via_acesso,
		cd_intervalo,
		coalesce(ie_urgencia,'N'),
		coalesce(ie_acm,'N'),
		coalesce(ie_se_necessario,'N'),
		ie_via_aplicacao,
		ie_bomba_infusao,
		ds_justificativa,
		ds_observacao,
		nr_ocorrencia,
		nr_sequencia,
		ds_diluicao_edit,
		cd_perfil_ativo
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and		ie_agrupador = 8;


BEGIN

select 	max(coalesce(ie_prescr_emergencia,'N')),
		max(dt_liberacao),
		max(nm_usuario_lib_enf),
		max(cd_medico)
into STRICT	ie_retrogrado_w,
		dt_liberacao_enf_w,
		nm_usuario_lib_enf_w,
		cd_medico_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;		

open c01;
loop
fetch c01 into	cd_material_w,
				cd_unidade_medida_w,
				qt_dose_w,
				ds_horarios_w,
				hr_prim_horario_w,
				qt_hora_aplicacao_w,
				qt_vel_infusao_w,
				nr_seq_via_acesso_w,
				cd_intervalo_w,
				ie_urgencia_w,
				ie_acm_w,
				ie_se_necessario_w,
				ie_via_aplicacao_w,
				ie_bomba_infusao_w,
				ds_justificativa_w,
				ds_observacao_w,
				nr_ocorrencia_w,
				nr_seq_material_w,
				ds_diluicao_edit_w,
				cd_perfil_ativo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	if (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
		dt_prim_horario_w := trunc(ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_inicio_prescr_p, hr_prim_horario_w), 'mi');
		if (dt_prim_horario_w < dt_inicio_prescr_p) then
			dt_prim_horario_w := dt_prim_horario_w + 1;
		end if;
	else
		dt_prim_horario_w := dt_inicio_prescr_p;
	end if;

	SELECT * FROM CPOE_Calcula_horarios_etapas(	nm_usuario_p, dt_prim_horario_w, 'S', nr_ocorrencia_w, cd_intervalo_w, 24, 0, 0, 'N') INTO STRICT nr_ocorrencia_w, ds_horarios_w;
	
	ie_administracao_w := 'P';
	if (ie_acm_w = 'S') then
		ie_administracao_w	:= 'C';
		ds_horarios_w		:= '';
		hr_prim_horario_w	:= '';
	elsif (ie_se_necessario_w = 'S') then
		ie_administracao_w	:= 'N';
		ds_horarios_w		:= '';
		hr_prim_horario_w	:= '';
	end if;
	
	if (coalesce(qt_hora_aplicacao_w,0) > 0) then
		qt_tempo_aplic_w := qt_hora_aplicacao_w||':00';
	else
		qt_tempo_aplic_w := '00:00';
	end if;
		
	select	nextval('cpoe_dieta_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into cpoe_dieta(
				nr_sequencia,
				nr_atendimento,
				ie_tipo_dieta,
				ie_administracao,
				ie_duracao,
				ie_continuo,
				ie_referencia,
				dt_inicio,
				dt_fim,
				dt_prox_geracao,
				dt_liberacao,
				cd_material,
				hr_prim_horario,
				cd_unidade_medida_dose,
				qt_dose,
				ds_horarios,
				qt_hora_aplicacao,
				qt_tempo_aplic,
				qt_vel_infusao,
				nr_seq_via_acesso,
				cd_intervalo,
				ie_urgencia,
				ie_acm,
				ie_se_necessario,
				ie_via_aplicacao,
				ie_bomba_infusao,
				ds_justificativa,
				ds_observacao,
				nr_ocorrencia,
				ds_orientacao,
				nm_usuario,
				nm_usuario_nrec,
				dt_atualizacao,
				dt_atualizacao_nrec,
				cd_perfil_ativo,
				cd_pessoa_fisica,
				cd_funcao_origem,
				cd_setor_atendimento,
				ie_retrogrado,
				dt_liberacao_enf,
				nm_usuario_lib_enf,
				cd_medico)
			values (
				nr_sequencia_w,
				nr_atendimento_p,
				'E',
				ie_administracao_w,
				'P',
				'I',
				'I',
				dt_prim_horario_w,
				dt_validade_prescr_p,
				dt_prim_horario_w + 12/24,
				dt_liberacao_p,
				cd_material_w,
				hr_prim_horario_w,
				cd_unidade_medida_w,
				qt_dose_w,
				ds_horarios_w,
				qt_hora_aplicacao_w,
				qt_tempo_aplic_w,
				qt_vel_infusao_w,
				nr_seq_via_acesso_w,
				cd_intervalo_w,
				CASE WHEN ie_urgencia_w='S' THEN '0'  ELSE null END ,
				ie_acm_w,
				ie_se_necessario_w,
				ie_via_aplicacao_w,
				ie_bomba_infusao_w,
				ds_justificativa_w,
				ds_observacao_w,
				nr_ocorrencia_w,
				ds_diluicao_edit_w,
				nm_usuario_p,
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp(),
				cd_perfil_ativo_w,
				cd_pessoa_fisica_p,
				cd_funcao_origem_p,
				cd_setor_atendimento_p,
				ie_retrogrado_w,
				dt_liberacao_enf_w,
				nm_usuario_lib_enf_w,
				cd_medico_w);

	update	prescr_material
	set		nr_seq_dieta_cpoe = nr_sequencia_w
	where	nr_sequencia = nr_seq_material_w
	and		nr_prescricao = nr_prescricao_p;
				
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_rep_gerar_dieta_enteral ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, dt_inicio_prescr_p timestamp, dt_validade_prescr_p timestamp, dt_liberacao_p timestamp, nm_usuario_p usuario.nm_usuario%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, cd_funcao_origem_p funcao.cd_funcao%type, cd_setor_atendimento_p prescr_medica.cd_setor_atendimento%type default null) FROM PUBLIC;

