CREATE OR REPLACE PROCEDURE cpoe_rep_gerar_recomendacao ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, dt_inicio_prescr_p timestamp, dt_validade_prescr_p timestamp, dt_liberacao_p timestamp, nm_usuario_p usuario.nm_usuario%type, cd_funcao_origem_p funcao.cd_funcao%type, cd_setor_atendimento_p prescr_medica.cd_setor_atendimento%type default null) AS $body$
DECLARE

					
dt_prim_horario_w		prescr_recomendacao.dt_inicio%type;
nr_seq_recomendacao_w	prescr_recomendacao.nr_sequencia%type;
cd_recomendacao_w		prescr_recomendacao.cd_recomendacao%type;
nr_seq_topografia_w		prescr_recomendacao.nr_seq_topografia%type;
ds_recomendacao_w		prescr_recomendacao.ds_recomendacao%type;
ie_acm_w				prescr_recomendacao.ie_acm%type;
ie_se_necessario_w		prescr_recomendacao.ie_se_necessario%type;
ie_urgencia_w			prescr_recomendacao.ie_urgencia%type;
cd_intervalo_w			prescr_recomendacao.cd_intervalo%type;
hr_prim_horario_w		prescr_recomendacao.hr_prim_horario%type;
ds_horarios_w			prescr_recomendacao.ds_horarios%type;
nr_ocorrencia_w			prescr_recomendacao.nr_ocorrencia%type;
cd_perfil_ativo_w		prescr_recomendacao.cd_perfil_ativo%type;

nr_sequencia_w			cpoe_recomendacao.nr_sequencia%type;
ie_administracao_w		cpoe_recomendacao.ie_administracao%type;

ie_retrogrado_w			prescr_medica.ie_prescr_emergencia%type;
nr_seq_atend_w			prescr_medica.nr_seq_atend%type;
cd_pessoa_fisica_w		prescr_medica.cd_pessoa_fisica%type;
dt_liberacao_enf_w		timestamp;
nm_usuario_lib_enf_w		prescr_medica.nm_usuario_lib_enf%type;
ds_observacao_w			cpoe_recomendacao.ds_observacao%type;
cd_medico_w				prescr_medica.cd_medico%type;

c01 CURSOR FOR					
SELECT	nr_sequencia,
		cd_recomendacao,
		ds_recomendacao,
		nr_seq_topografia,
		coalesce(ie_acm,'N'),
		coalesce(ie_se_necessario,'N'),
		coalesce(ie_urgencia,'N'),
		cd_intervalo,
		nr_ocorrencia,
		hr_prim_horario,
		ds_horarios,
		cd_perfil_ativo,
		ds_observacao
from	prescr_recomendacao
where	nr_prescricao = nr_prescricao_p
and 	(cd_recomendacao IS NOT NULL AND cd_recomendacao::text <> '')
and		coalesce(nr_seq_rec_cpoe::text, '') = '';


BEGIN

select	max(coalesce(ie_prescr_emergencia,'N')),
		max(nr_seq_atend),
		max(cd_pessoa_fisica),
		max(dt_liberacao),
		max(nm_usuario_lib_enf),
		max(cd_medico)
into STRICT	ie_retrogrado_w,
		nr_seq_atend_w,
		cd_pessoa_fisica_w,
		dt_liberacao_enf_w,
		nm_usuario_lib_enf_w,
		cd_medico_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

open c01;
loop
fetch c01 into	nr_seq_recomendacao_w,
				cd_recomendacao_w,
				ds_recomendacao_w,
				nr_seq_topografia_w,
				ie_acm_w,
				ie_se_necessario_w,
				ie_urgencia_w,
				cd_intervalo_w,
				nr_ocorrencia_w,
				hr_prim_horario_w,
				ds_horarios_w,
				cd_perfil_ativo_w,
				ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (hr_prim_horario_w IS NOT NULL AND hr_prim_horario_w::text <> '') then
			dt_prim_horario_w := ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_inicio_prescr_p, hr_prim_horario_w);
		if (dt_prim_horario_w < dt_inicio_prescr_p) and (ie_urgencia_w = 'N') then
			dt_prim_horario_w := dt_prim_horario_w +1;
		end if;
	end if;
	
	ie_administracao_w := 'P';
	if (ie_se_necessario_w = 'S') then
		ie_administracao_w := 'N';
		ds_horarios_w := '';
	elsif (ie_acm_w = 'S') then
		ie_administracao_w := 'C';
		ds_horarios_w := '';
	end if;
	
	select	nextval('cpoe_recomendacao_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into cpoe_recomendacao(
				nr_sequencia,
				nr_atendimento,
				cd_recomendacao,
				ds_recomendacao,
				nr_seq_topografia,
				ie_administracao,
				ie_duracao,
				hr_prim_horario,
				dt_inicio,
				dt_fim,
				dt_prox_geracao,
				cd_intervalo,
				ds_horarios,
				nr_ocorrencia,
				ie_acm,
				ie_se_necessario,
				ie_urgencia,
				dt_liberacao,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				cd_perfil_ativo,
				cd_funcao_origem,
				cd_setor_atendimento,
				ie_retrogrado,
				ie_oncologia,
				cd_pessoa_fisica,
				dt_liberacao_enf,
				nm_usuario_lib_enf,
				ds_observacao,
				cd_medico)
			values (
				nr_sequencia_w,
				nr_atendimento_p,
				cd_recomendacao_w,
				ds_recomendacao_w,
				nr_seq_topografia_w,
				ie_administracao_w,
				'P',
				hr_prim_horario_w,
				dt_prim_horario_w,				
				dt_validade_prescr_p,
				dt_prim_horario_w + 12/24,
				cd_intervalo_w,
				ds_horarios_w,
				nr_ocorrencia_w,
				ie_acm_w,
				ie_se_necessario_w,
				CASE WHEN ie_urgencia_w='S' THEN '0'  ELSE null END ,
				dt_liberacao_p,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				cd_perfil_ativo_w,
				cd_funcao_origem_p,
				cd_setor_atendimento_p,
				ie_retrogrado_w,
				CASE WHEN coalesce(nr_seq_atend_w::text, '') = '' THEN 'N'  ELSE 'S' END ,
				cd_pessoa_fisica_w,
				dt_liberacao_enf_w,
				nm_usuario_lib_enf_w,
				ds_observacao_w,
				cd_medico_w);
					
	update	prescr_recomendacao
	set		nr_seq_rec_cpoe = nr_sequencia_w
	where	nr_sequencia = nr_seq_recomendacao_w
	and		nr_prescricao = nr_prescricao_p;
	
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
