SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_shoppingcart_ap (nr_sequencia_p bigint, ie_retrogrado_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_count_w	bigint;
ie_return_value_w	varchar(1) := 'N';

c01 CURSOR FOR
SELECT  a.*
from 	cpoe_anatomia_patologica a
where	nr_sequencia = nr_sequencia_p;

BEGIN
ds_retorno_p := 'S';

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	for r_c01_w in C01
		loop

		if (coalesce(r_c01_w.nr_seq_proc_interno::text, '') = '' and coalesce(r_c01_w.dt_inicio::text, '') = '') then
			ds_retorno_p := 'N';
			goto return_value;
		end if;
		
		if (r_c01_w.dt_inicio < clock_timestamp()) then
			ds_retorno_p := 'N';
			goto return_value;
		end if;

    select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  ie_duplicado
		into STRICT	ie_return_value_w
		from	cpoe_anatomia_patologica a
		where	a.nr_sequencia <> r_c01_w.nr_sequencia
    and   a.nr_seq_proc_interno = r_c01_w.nr_seq_proc_interno
		and		((nr_atendimento = r_c01_w.nr_atendimento) or (cd_pessoa_fisica = r_c01_w.cd_pessoa_fisica and coalesce(nr_atendimento::text, '') = ''))
		and		(((dt_lib_suspensao IS NOT NULL AND dt_lib_suspensao::text <> '') and (dt_suspensao > clock_timestamp())) or (coalesce(dt_lib_suspensao::text, '') = ''))
		and 	((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (nm_usuario = wheb_usuario_pck.get_nm_usuario))
		and		obter_se_cpoe_regra_duplic('AP', wheb_usuario_pck.get_cd_perfil, r_c01_w.cd_setor_atendimento) = 'S'
		and 	((a.dt_inicio between r_c01_w.dt_inicio and r_c01_w.dt_fim) or (a.dt_fim between r_c01_w.dt_inicio and r_c01_w.dt_fim) or (coalesce(a.dt_fim::text, '') = '' and a.dt_inicio < r_c01_w.dt_inicio) or (a.dt_fim > r_c01_w.dt_fim and a.dt_inicio < r_c01_w.dt_inicio) or	
				 ((a.dt_inicio >  r_c01_w.dt_inicio) or (r_c01_w.dt_inicio <= a.dt_inicio  and r_c01_w.dt_fim > a.dt_inicio)) or (a.ie_retrogrado = 'S' and coalesce(a.dt_liberacao::text, '') = ''));

		if (ie_return_value_w = 'S') then
			ds_retorno_p := 'N';
			goto return_value;
		end if;

	end loop;
end if;
			
<<return_value>>
 null;
exception when others then
	CALL gravar_log_cpoe(substr('CPOE_SHOPPINGCART_AP EXCEPTION:'|| substr(to_char(sqlerrm),1,2000) || '//nr_sequencia_p: '|| nr_sequencia_p,1,40000));
	ds_retorno_p := 'N';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cpoe_shoppingcart_ap (nr_sequencia_p bigint, ie_retrogrado_p text, ds_retorno_p INOUT text) FROM PUBLIC;

