CREATE OR REPLACE PROCEDURE cpoe_shoppingcart_nutrition (nr_sequencia_p bigint, ie_retrogrado_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_count_w	bigint;
ie_return_value_w	varchar(1);

c01 CURSOR FOR
SELECT  a.ie_tipo_dieta
from 	cpoe_dieta a
where	nr_sequencia = nr_sequencia_p;

BEGIN

ds_retorno_p := 'S';

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	for r_c01_w in C01
	loop
		if (r_c01_w.ie_tipo_dieta = 'O') then
			ds_retorno_p := cpoe_shoppingcart_oral(nr_sequencia_p, ie_retrogrado_p, ds_retorno_p);
		elsif (r_c01_w.ie_tipo_dieta = 'E') then
			ds_retorno_p := cpoe_shoppingcart_enteral(nr_sequencia_p, ie_retrogrado_p, ds_retorno_p);
		elsif (r_c01_w.ie_tipo_dieta = 'S') then
			ds_retorno_p := cpoe_shoppingcart_suplemento(nr_sequencia_p, ie_retrogrado_p, ds_retorno_p);
		elsif (r_c01_w.ie_tipo_dieta = 'J') then
			ds_retorno_p := cpoe_shoppingcart_jejum(nr_sequencia_p, ie_retrogrado_p, ds_retorno_p);
		elsif (r_c01_w.ie_tipo_dieta = 'L') then
			ds_retorno_p := cpoe_shoppingcart_leite(nr_sequencia_p, ie_retrogrado_p, ds_retorno_p);
		end if;
	end loop;
end if;

exception when others then
	CALL gravar_log_cpoe(substr('CPOE_SHOPPINGCART_NUTRITION EXCEPTION:'|| substr(to_char(sqlerrm),1,2000) || '//nr_sequencia_p: '|| nr_sequencia_p,1,40000));
	ds_retorno_p := 'N';
end;
