CREATE OR REPLACE PROCEDURE cpoe_suspend_selected_times (nr_seq_cpoe_rp_p bigint, dt_horario_p timestamp, nr_seq_prescr_mat_hor_p bigint, nm_usuario_p text) AS $body$
DECLARE


c_times CURSOR FOR
  SELECT  b.nr_sequencia
  FROM    prescr_mat_hor b,
          prescr_material a,
          cpoe_material d
  WHERE   a.nr_prescricao = b.nr_prescricao
  AND     a.nr_sequencia = b.nr_seq_material
  AND     a.nr_seq_mat_cpoe = d.nr_sequencia
  AND     d.nr_seq_cpoe_rp= nr_seq_cpoe_rp_p
  and     b.dt_horario  = dt_horario_p
  and     (nr_seq_cpoe_rp_p IS NOT NULL AND nr_seq_cpoe_rp_p::text <> '')
  
union all

  SELECT  b.nr_sequencia
  FROM    prescr_mat_hor b
  WHERE   b.nr_sequencia = nr_seq_prescr_mat_hor_p
  and     (nr_seq_prescr_mat_hor_p IS NOT NULL AND nr_seq_prescr_mat_hor_p::text <> '');

BEGIN

for r_c_times in c_times loop
  CALL cpoe_susp_prescr_mat_hor(r_c_times.nr_sequencia, nm_usuario_p, null, null, null, 'S');
end loop;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
