CREATE OR REPLACE PROCEDURE cpoe_susp_medication_hor ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_material_p prescr_material.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_start_w		timestamp;
dt_end_w		timestamp;
nr_seq_mat_cpoe_w	prescr_material.nr_seq_mat_cpoe%type;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	
	select	coalesce(max(a.nr_seq_mat_cpoe),0)
	into STRICT	nr_seq_mat_cpoe_w
	from	prescr_material a
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_sequencia	= nr_seq_material_p;

	select	max(a.dt_start),
		max(a.dt_end)
	into STRICT	dt_start_w,
		dt_end_w
	from	cpoe_susp_medication a
	where	coalesce(a.dt_inactive::text, '') = ''
	and	a.nr_seq_mat_cpoe = nr_seq_mat_cpoe_w;

	if (dt_start_w IS NOT NULL AND dt_start_w::text <> '') and (dt_end_w IS NOT NULL AND dt_end_w::text <> '') then
		update	prescr_mat_hor
		set	dt_suspensao	= clock_timestamp(),
			nm_usuario_susp	= nm_usuario_p
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_material = nr_seq_material_p
		and	dt_horario between dt_start_w and dt_end_w
		and	coalesce(dt_suspensao::text, '') = '';

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
