CREATE OR REPLACE PROCEDURE cpoe_susp_prescr_dieta_hor ( nr_sequencia_p bigint, nm_usuario_p text, ie_prescr_alteracao_p text default 'N') AS $body$
DECLARE

			 
nr_prescricao_w			prescr_medica.nr_prescricao%type;
nr_seq_dieta_w			prescr_dieta_hor.nr_seq_dieta%type;
nr_seq_alteracao_w		prescr_mat_alteracao.nr_sequencia%type;
ds_justificativa_w		prescr_mat_alteracao.ds_justificativa%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
cd_dieta_w				prescr_dieta.cd_dieta%type;
ie_gravar_justif_w		varchar(1);
dt_horario_w			timestamp;


BEGIN 
 
ie_gravar_justif_w := Obter_Param_Usuario(924, 890, obter_perfil_ativo, nm_usuario_p, 0, ie_gravar_justif_w);
 
update	prescr_dieta_hor 
set		dt_suspensao		= clock_timestamp(), 
		nm_usuario_susp		= nm_usuario_p 
where	nr_sequencia 		= nr_sequencia_p 
and		coalesce(dt_fim_horario::text, '') = '' 
and		coalesce(dt_suspensao::text, '') = '';
 
select	max(a.nr_prescricao), 
		max(a.nr_seq_dieta), 
		max(a.dt_horario), 
		max(b.nr_atendimento), 
		max(b.cd_estabelecimento), 
		max(c.cd_dieta), 
		CASE WHEN ie_gravar_justif_w='S' THEN  PERFORM obter_desc_expressao(727956) END  
into STRICT	nr_prescricao_w, 
		nr_seq_dieta_w, 
		dt_horario_w, 
		nr_atendimento_w, 
		cd_estabelecimento_w, 
		cd_dieta_w, 
		ds_justificativa_w 
from	prescr_dieta_hor a, 
		prescr_medica b, 
		prescr_dieta c 
where	a.nr_sequencia = nr_sequencia_p 
and		a.nr_prescricao = b.nr_prescricao 
and		a.nr_prescricao = c.nr_prescricao 
and		a.nr_seq_dieta = c.nr_sequencia;
 
update	prescr_dieta 
set		ie_horario_susp	= 'S' 
where	nr_prescricao	= nr_prescricao_w 
and		nr_sequencia	= nr_seq_dieta_w;
 
 
 
if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and ie_prescr_alteracao_p = 'S') then 
 
	CALL gerar_alter_hor_prescr_adep( nr_atendimento_w, 'D', cd_dieta_w, 
						nr_prescricao_w, nr_seq_dieta_w, nr_sequencia_p, 
						dt_horario_w, 5, ds_justificativa_w, null, null, nm_usuario_p);
end if;
 
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
