CREATE OR REPLACE PROCEDURE cpoe_update_insurance ( nr_sequencia_p cpoe_order_unit.nr_sequencia%type, nr_seq_nais_insurance_p cpoe_order_unit.nr_seq_nais_insurance%type) AS $body$
DECLARE


nr_seq_insurance_w      nais_insurance.nr_sequencia%type;
cd_pessoa_fisica_w      cpoe_order_unit.cd_pessoa_fisica%type;


BEGIN

select max(cd_pessoa_fisica)
into STRICT cd_pessoa_fisica_w
from cpoe_order_unit b
where b.nr_sequencia = nr_sequencia_p;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

select max(nr_sequencia)
into STRICT nr_seq_insurance_w
from nais_insurance a
where exists (SELECT 1
  from convenio x
  where x.cd_externo = to_char(a.cd_insurance_pattern)
  )
and a.cd_pessoa_fisica = cd_pessoa_fisica_w;

update  cpoe_order_unit
set		nr_seq_nais_insurance = coalesce(nr_seq_insurance_w, get_jp_insurance_default(cd_pessoa_fisica))
where	nr_sequencia = nr_sequencia_p;

end if;

end;
$body$
