SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_update_liberacao_rep (nr_prescricao_p prescr_medica.nr_prescricao%type) AS $body$
DECLARE


nr_seq_item_cpoe_w	cpoe_material.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	nr_seq_mat_cpoe
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and		(nr_seq_mat_cpoe IS NOT NULL AND nr_seq_mat_cpoe::text <> '');
	
c02 CURSOR FOR
	SELECT	nr_seq_rec_cpoe
	from	prescr_recomendacao
	where	nr_prescricao	= nr_prescricao_p
	and		(nr_seq_rec_cpoe IS NOT NULL AND nr_seq_rec_cpoe::text <> '');
	
c03 CURSOR FOR
	SELECT	nr_seq_proc_cpoe
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and		(nr_seq_proc_cpoe IS NOT NULL AND nr_seq_proc_cpoe::text <> '');


BEGIN

open c01;
loop
fetch c01 into	nr_seq_item_cpoe_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	update	cpoe_material
	set		dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_seq_item_cpoe_w;	
	
	end;
end loop;
close c01;

open c02;
loop
fetch c02 into	nr_seq_item_cpoe_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin
	
	update	cpoe_recomendacao
	set		dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_seq_item_cpoe_w;	
	
	end;
end loop;
close c02;

open c03;
loop
fetch c03 into	nr_seq_item_cpoe_w;
EXIT WHEN NOT FOUND; /* apply on c03 */
	begin
	
	update	cpoe_procedimento
	set		dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_seq_item_cpoe_w;	
	
	end;
end loop;
close c03;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
