CREATE OR REPLACE PROCEDURE cpoe_update_nao_padronizados ( nm_usuario_p text, nr_seq_cpoe_p bigint, nr_prescricao_p bigint, qt_total_dias_lib_p bigint) AS $body$
DECLARE


ie_dias_util_medic_w			material.ie_dias_util_medic%type;
nr_seq_mat_cpoe_w	cpoe_material.nr_sequencia%type;
dt_fim_w  	  	timestamp;
--Pragma Autonomous_Transaction;
BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_cpoe_p,0) > 0) then

		select  max(a.dt_inicio) + coalesce(qt_total_dias_lib_p, 0),
				coalesce(max(b.ie_dias_util_medic), 'N')
		into STRICT	dt_fim_w,
				ie_dias_util_medic_w
		from	cpoe_material a,
				material b
		where	nr_sequencia = nr_seq_cpoe_p
		and		a.cd_material = b.cd_material;

		update	cpoe_material
		set		qt_total_dias_lib	= qt_total_dias_lib_p,
				dt_fim_nao_padrao 	= dt_fim_w,
				dt_fim				= CASE WHEN ie_dias_util_medic_w='N' THEN  dt_fim_w  ELSE dt_fim END ,
				nm_usuario			= nm_usuario_p
		where	nr_sequencia 		= nr_seq_cpoe_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
