CREATE OR REPLACE PROCEDURE cpoe_update_proc_evaluation ( nr_seq_cpoe_p bigint, nr_seq_avaliacao_p bigint, ie_tipo_item_p text) AS $body$
BEGIN

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and (nr_seq_avaliacao_p IS NOT NULL AND nr_seq_avaliacao_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '')   then

   if ( ie_tipo_item_p = 'P' )  then
    	update cpoe_procedimento
    	set	nr_seq_avaliacao = nr_seq_avaliacao_p
    	where nr_sequencia   = nr_seq_cpoe_p;
	end if;
	
	if ( ie_tipo_item_p = 'M' )  then
    	update cpoe_material
    	set	nr_seq_avaliacao = nr_seq_avaliacao_p
    	where nr_sequencia   = nr_seq_cpoe_p;
	end if;

    commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
