SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cpoe_update_urgency_rotine ( nr_atendimento_p bigint, nr_sequencia_p bigint, ie_tipo_item_p text, ie_urgencia_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE

						 
ie_urgencia_w				varchar(3);
dt_inicio_w					timestamp;
nr_ocorrencia_w				double precision;
nr_seq_proc_interno_w		proc_interno.nr_sequencia%type;
ds_horarios_w				varchar(2000);
ds_horarios_aux_w			varchar(2000);
cd_intervalo_w				intervalo_prescricao.cd_intervalo%type;
qt_min_intervalo_w			intervalo_prescricao.qt_min_intervalo%type;
cd_procedimento_w			procedimento.cd_procedimento%type;
ie_origem_proced_w			procedimento.ie_origem_proced%type;

 
 

BEGIN 
 
if (coalesce(ie_urgencia_p,'N') = 'S') then 
	ie_urgencia_w := '0';
	dt_inicio_w := trunc(clock_timestamp(),'mi');
else 
	ie_urgencia_w := null;
	dt_inicio_w := trunc(clock_timestamp(),'hh') + 1/24;
end if;
 
if (ie_tipo_item_p = 'P') then 
 
select	max(nr_seq_proc_interno), 
		max(cd_intervalo) 
into STRICT	nr_seq_proc_interno_w, 
		cd_intervalo_w 
from	cpoe_procedimento 
where	nr_sequencia = nr_sequencia_p 
and		nr_atendimento = nr_atendimento_p;
 
select	 max(coalesce(qt_min_intervalo,0)) 
into STRICT	 qt_min_intervalo_w 
from	 intervalo_prescricao 
where	 cd_intervalo = cd_intervalo_w;
 
SELECT * FROM Obter_proc_tab_interno( nr_seq_proc_interno_w, null, nr_atendimento_p, 0, cd_procedimento_w, ie_origem_proced_w, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
	 
nr_ocorrencia_w	:= 0;
SELECT * FROM cpoe_calcular_horario_prescr(	nr_atendimento_p, cd_intervalo_w, null, dt_inicio_w, null, qt_min_intervalo_w, nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null, null, cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios_aux_w;
 
ds_horarios_w	:= substr(substr(ds_horarios_w,1,255) || substr(ds_horarios_aux_w,1,255),1,2000);
 
 
update	cpoe_procedimento 
set		ds_horarios = ds_horarios_w, 
		hr_prim_horario = to_char(dt_inicio_w,'hh24:mi'), 
		dt_inicio	= dt_inicio_w, 
		ie_urgencia = ie_urgencia_w 
where	nr_sequencia = nr_sequencia_p;
 
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
