CREATE OR REPLACE PROCEDURE cpoe_verify_dosage_fractional ( cd_material_p cpoe_dieta.cd_material%type, qt_dose_p cpoe_dieta.qt_dose%type, cd_unidade_medida_p cpoe_dieta.cd_unidade_medida_dose%type, qt_vel_infusao_p cpoe_dieta.qt_vel_infusao%type, qt_tempo_aplic_p cpoe_dieta.qt_tempo_aplic%type, qt_nova_dose_p INOUT cpoe_dieta.qt_dose%type, qt_novo_tempo_aplic_p INOUT cpoe_dieta.qt_tempo_aplic%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type ) AS $body$
DECLARE


	qt_dose_conv_w		cpoe_dieta.qt_dose%type;
	qt_dose_frac_w		cpoe_dieta.qt_dose%type;
	qt_dose_return_w	cpoe_dieta.qt_dose%type := 0;
    qt_tempo_return_w   cpoe_dieta.qt_tempo_aplic%type := '';
	qt_velocidade_w		cpoe_dieta.qt_vel_infusao%type;
	qt_result_w			double precision;
	qt_minutos_w	    double precision;
	qt_horas_w			bigint;
	qt_min_w			varchar(2000);
	param_CPOE_24_w		varchar(2);


BEGIN
	param_CPOE_24_w := Obter_Param_Usuario(2314, 24, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, param_CPOE_24_w);

    if ((param_CPOE_24_w = 'S') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (qt_dose_p IS NOT NULL AND qt_dose_p::text <> '') and (cd_unidade_medida_p IS NOT NULL AND cd_unidade_medida_p::text <> '') /*and (qt_tempo_aplic_p is not null)*/
) then

        qt_dose_conv_w := obter_conversao_unid_med_cons(cd_material_p, cd_unidade_medida_p, qt_dose_p);
        qt_dose_frac_w := qt_dose_conv_w - trunc(qt_dose_conv_w, 0);
		qt_minutos_w   := obter_minutos_hora('24:00'); /* Definido na OS 1873952 que não vai mais usar o que o médico informou na tela (qt_tempo_aplic_p), mas sim 24 hrs */
        if ((((upper(cd_unidade_medida_p) = 'FR') and (qt_dose_frac_w > 0)) or (qt_dose_frac_w > 0)) and (trunc(qt_dose_conv_w, 0) > 0)) then
            qt_dose_return_w := qt_dose_p - (obter_conversao_unid_med(cd_material_p, cd_unidade_medida_p) * qt_dose_frac_w);
        end if;

        if ((coalesce(qt_dose_return_w, 0) > 0) and (coalesce(qt_minutos_w, 0) > 0)) then
            qt_velocidade_w := round((dividir(Obter_conversao_ml(cd_material_p, qt_dose_p, cd_unidade_medida_p), qt_minutos_w) * 60)::numeric, 2);

			if (coalesce(qt_velocidade_w, 0) > 0) then
				qt_result_w := dividir(Obter_conversao_ml(cd_material_p, qt_dose_return_w, cd_unidade_medida_p), qt_velocidade_w);
				qt_horas_w := trunc((qt_result_w / 24) * 24);
				qt_min_w   := round((((qt_result_w / 24) * 24) - qt_horas_w) * 60 );			
				qt_tempo_return_w := formata_hora(qt_horas_w || ':' || qt_min_w);
			else
				qt_tempo_return_w := 'N';
				qt_dose_return_w  := 0;				
			end if;
        end if;
	end if;

    qt_novo_tempo_aplic_p := qt_tempo_return_w;
    qt_nova_dose_p := qt_dose_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
