CREATE OR REPLACE PROCEDURE cpoe_withhold_dose ( nr_seq_mat_cpoe_p bigint, dt_start_p timestamp, dt_end_p timestamp, nm_usuario_p text, ie_action_p text, dt_reference_p timestamp) AS $body$
DECLARE

/*
ie_action_p: W - Withhold, U - Undo withhold
*/
BEGIN

if (nr_seq_mat_cpoe_p IS NOT NULL AND nr_seq_mat_cpoe_p::text <> '') then
	if (ie_action_p = 'W') then
		if (dt_start_p IS NOT NULL AND dt_start_p::text <> '') and (dt_end_p IS NOT NULL AND dt_end_p::text <> '') then

			insert into cpoe_susp_medication(
					nr_seq_mat_cpoe,
					nm_usuario,
					nm_usuario_nrec,
					dt_atualizacao,
					dt_atualizacao_nrec,
					dt_start,
					dt_end,
					dt_inactive)
			values (	nr_seq_mat_cpoe_p,
					nm_usuario_p,
					nm_usuario_p,
					clock_timestamp(),
					clock_timestamp(),
					dt_start_p,
					dt_end_p,
					null);
			
			commit;
			
		end if;
	elsif (ie_action_p = 'U') then
		
		update	cpoe_susp_medication
		set	dt_inactive = clock_timestamp()
		where	nr_seq_mat_cpoe = nr_seq_mat_cpoe_p
		and	trunc(dt_reference_p) between trunc(dt_start) and trunc(dt_end)
		and	coalesce(dt_inactive::text, '') = '';
		
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
