CREATE OR REPLACE PROCEDURE cp_create_guidance ( nr_seq_interv_plan_p cp_interv_plan_guid.nr_seq_interv_plan%type, nr_seq_indic_measure_p cp_indicator_measure_guid.nr_seq_indic_measure%type, ds_guidance_p cp_guidance.ds_guidance%type, ds_version_p cp_guidance.ds_version%type, nm_usuario_p cp_guidance.nm_usuario%type ) AS $body$
DECLARE


nr_sequencia_w			cp_guidance.nr_sequencia%type;
nr_seq_interv_plan_guid_w	cp_interv_plan_guid.nr_sequencia%type;
nr_seq_indic_measure_guid_w	cp_indicator_measure_guid.nr_sequencia%type;


BEGIN

select	nextval('cp_guidance_seq')
into STRICT	nr_sequencia_w
;

insert into cp_guidance(
	nr_sequencia,
	ds_version,
	ds_guidance,
	ie_origin,
	si_import_status,
	ie_situacao,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec
) values (
	nr_sequencia_w,
	ds_version_p,
	ds_guidance_p,
	'E', -- From Elsevier
	'SP',
	'I',
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp()
);

if (nr_seq_interv_plan_p IS NOT NULL AND nr_seq_interv_plan_p::text <> '') then
	begin
		select	nr_sequencia
		into STRICT	nr_seq_interv_plan_guid_w
		from	cp_interv_plan_guid
		where	nr_seq_interv_plan = nr_seq_interv_plan_p
			and nr_seq_guidance = nr_sequencia_w;
	exception
		when no_data_found then
			nr_seq_interv_plan_guid_w	:= null;
	end;
	
	if (coalesce(nr_seq_interv_plan_guid_w::text, '') = '') then
		select	nextval('cp_interv_plan_guid_seq')
		into STRICT	nr_seq_interv_plan_guid_w
		;
		
		insert into cp_interv_plan_guid(
			nr_sequencia,
			nr_seq_interv_plan,
			nr_seq_guidance,
			ie_origin,
			si_import_status,
			ie_situacao,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec
		) values (
			nr_seq_interv_plan_guid_w,
			nr_seq_interv_plan_p,
			nr_sequencia_w,
			'E', -- From Elsevier
			'SP',
			'I',
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp()
		);
	end if;
end if;

if (nr_seq_indic_measure_p IS NOT NULL AND nr_seq_indic_measure_p::text <> '') then
	begin
		select	nr_sequencia
		into STRICT	nr_seq_indic_measure_guid_w
		from	cp_indicator_measure_guid
		where	nr_seq_indic_measure = nr_seq_indic_measure_p
			and nr_seq_guidance = nr_sequencia_w;
	exception
		when no_data_found then
			nr_seq_indic_measure_guid_w	:= null;
	end;

	if (coalesce(nr_seq_indic_measure_guid_w::text, '') = '') then
		select	nextval('cp_indicator_measure_guid_seq')
		into STRICT	nr_seq_indic_measure_guid_w
		;
		
		insert into cp_indicator_measure_guid(
			nr_sequencia,
			nr_seq_indic_measure,
			nr_seq_guidance,
			ie_origin,
			si_import_status,
			ie_situacao,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec
		) values (
			nr_seq_indic_measure_guid_w,
			nr_seq_indic_measure_p,
			nr_sequencia_w,
			'E', -- From Elsevier
			'SP',
			'I',
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp()
		);
	end if;
end if;

end;
$body$
