SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cp_create_interv_item ( nr_seq_intervention_p cp_intervention_item_assoc.nr_seq_intervention%type, ds_unique_value_p cp_intervention_item.ds_unique_value%type, ds_display_name_p cp_intervention_item.ds_display_name%type, ds_caption_color_p cp_intervention_item.ds_caption_color%type, nr_seq_order_p cp_intervention_item.nr_seq_order%type, ie_language_p cp_intervention_item.ie_language%type, ds_version_p cp_intervention_item.ds_version%type, ie_active_p cp_intervention_item.ie_active%type, nm_usuario_p cp_intervention_item.nm_usuario%type ) AS $body$
DECLARE


nr_sequencia_w				cp_intervention_item.nr_sequencia%type;
ds_version_w				cp_intervention_item.ds_version%type;
nr_seq_interv_item_assoc_w	cp_intervention_item_assoc.nr_sequencia%type;
ds_display_name_w			cp_intervention_item.ds_display_name%type;
si_record_changed_w			cp_intervention_item.si_record_changed%type;


BEGIN

begin
	select	nr_sequencia,
			ds_version,
			ds_display_name
	into STRICT	nr_sequencia_w,
			ds_version_w,
			ds_display_name_w
	from	cp_intervention_item
	where	nr_sequencia = (
		SELECT	max(nr_sequencia)
		from	cp_intervention_item
		where ds_unique_value = ds_unique_value_p
	);
exception
	when no_data_found then
	begin
		nr_sequencia_w	:= null;
		ds_version_w	:= null;
	end;
end;

select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'Y' END
into STRICT	si_record_changed_w
from	cp_intervention_item
where	(ds_version IS NOT NULL AND ds_version::text <> '')
and		ds_version <> ds_version_p;

if (ds_version_w IS NOT NULL AND ds_version_w::text <> '') and (si_record_changed_w = 'Y') then

	select	CASE WHEN count(1)=0 THEN  'Y'  ELSE 'N' END
	into STRICT	si_record_changed_w
	from	cp_intervention_item
	where	ds_unique_value = ds_unique_value_p;
	
	if (si_record_changed_w = 'N') then
		select	CASE WHEN ds_display_name_p=coalesce(ds_display_name_w, ds_display_name_p) THEN  'N'  ELSE 'Y' END
		into STRICT	si_record_changed_w
		;
	end if;

end if;

update	cp_intervention_item_assoc
set		si_record_changed = 'N'
where 	nr_seq_interv_item = nr_sequencia_w;

if (coalesce(nr_sequencia_w::text, '') = '' or ds_version_w <> ds_version_p) then
	select	nextval('cp_intervention_item_seq')
	into STRICT	nr_sequencia_w
	;

	insert into cp_intervention_item(
		nr_sequencia,
		ds_unique_value,
		ds_version,
		ds_display_name,
		ds_caption_color,
		ie_language,
		ie_origin,
		nr_seq_order,
		ie_active,
		si_import_status,
		ie_situacao,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		si_record_changed
	) values (
		nr_sequencia_w,
		ds_unique_value_p,
		ds_version_p,
		ds_display_name_p,
		ds_caption_color_p,
		ie_language_p,
		'E', -- From Elsevier
		nr_seq_order_p,
		ie_active_p,
		'SP',
		'I',
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		coalesce(si_record_changed_w, 'N')
	);
	
	update	cp_intervention_item
	set		si_record_changed = 'N'
	where 	ds_version = ds_version_w;
end if;

if (nr_seq_intervention_p IS NOT NULL AND nr_seq_intervention_p::text <> '') then
	begin
		select	nr_sequencia
		into STRICT	nr_seq_interv_item_assoc_w
		from	cp_intervention_item_assoc
		where	nr_seq_intervention = nr_seq_intervention_p
			and nr_seq_interv_item = nr_sequencia_w;
	exception
		when no_data_found then
			nr_seq_interv_item_assoc_w	:= null;
	end;
	
	if (coalesce(nr_seq_interv_item_assoc_w::text, '') = '') then		
		
		select	nextval('cp_intervention_item_assoc_seq')
		into STRICT	nr_seq_interv_item_assoc_w
		;
		
		if (si_record_changed_w = 'N') then
		
			select 	CASE WHEN count(1)=0 THEN  'Y'  ELSE 'N' END
			into STRICT	si_record_changed_w
			from 	cp_intervention_item_assoc a,
					cp_intervention_item b
			where	b.nr_sequencia = a.nr_seq_interv_item
			and		b.nr_sequencia = (	SELECT 	max(nr_sequencia) 
										from 	cp_intervention_item 
										where 	ds_unique_value = ds_unique_value_p 
										and 	nr_sequencia < nr_sequencia_w)
			and		a.nr_seq_intervention = nr_seq_intervention_p;
			
		end if;

		insert into cp_intervention_item_assoc(
			nr_sequencia,
			nr_seq_intervention,
			nr_seq_interv_item,
			ie_origin,
			si_import_status,
			ie_situacao,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			si_record_changed
		) values (
			nr_seq_interv_item_assoc_w,
			nr_seq_intervention_p,
			nr_sequencia_w,
			'E', -- From Elsevier
			'SP',
			'I',
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			coalesce(si_record_changed_w, 'N')
		);	
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
