CREATE OR REPLACE PROCEDURE cp_create_keyword ( ds_keyword_p cp_keyword.ds_keyword%type, nr_seq_care_plan_p cp_keyword.nr_seq_care_plan%type, nm_usuario_p cp_keyword.nm_usuario%type ) AS $body$
DECLARE


nr_sequencia_w				cp_keyword.nr_sequencia%type;
ds_old_keyword_w			cp_keyword.ds_keyword%type;
si_record_changed_w			cp_keyword.si_record_changed%type;
nr_seq_old_care_plan_w		care_plan.nr_sequencia%type;


BEGIN

select	get_old_care_plan(nr_seq_care_plan_p)
into STRICT	nr_seq_old_care_plan_w
;

if (nr_seq_old_care_plan_w IS NOT NULL AND nr_seq_old_care_plan_w::text <> '') then

	select	CASE WHEN count(1)=0 THEN  'Y'  ELSE 'N' END
	into STRICT	si_record_changed_w
	from	cp_keyword
	where	nr_seq_care_plan = nr_seq_old_care_plan_w
	and		ds_keyword = ds_keyword_p;

end if;

select	nextval('cp_keyword_seq')
into STRICT	nr_sequencia_w
;

insert into cp_keyword(
	nr_sequencia,
	nr_seq_care_plan,
	ds_keyword,
	ie_origin,
	si_import_status,
	ie_situacao,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec
) values (
	nr_sequencia_w,
	nr_seq_care_plan_p,
	ds_keyword_p,
	'E', -- From Elsevier
	'SP',
	'I',
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp()
);

update	cp_keyword
set		si_record_changed = 'N'
where 	ds_keyword = ds_keyword_p
and		nr_seq_care_plan = nr_seq_old_care_plan_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
