SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cp_create_measure ( nr_seq_indicator_p cp_indicator_measure.nr_seq_indicator%type, ds_unique_value_p cp_measure.ds_unique_value%type, ds_display_name_p cp_measure.ds_display_name%type, ds_caption_color_p cp_measure.ds_caption_color%type, ds_version_p cp_measure.ds_version%type, nr_score_p cp_measure.nr_score%type, ie_language_p cp_measure.ie_language%type, nr_seq_order_p cp_measure.nr_seq_order%type, ie_active_p cp_measure.ie_active%type, nm_usuario_p cp_measure.nm_usuario%type, nr_sequencia_p INOUT cp_measure.nr_sequencia%type, nr_seq_indic_measure_p INOUT cp_indicator_measure.nr_sequencia%type, nr_seq_goal_indic_p cp_indicator_measure.nr_seq_goal_indic%type ) AS $body$
DECLARE


ds_version_w			cp_measure.ds_version%type;
ds_caption_color_w		cp_measure.ds_caption_color%type;
ds_display_name_w		cp_measure.ds_display_name%type;
si_record_changed_w		cp_measure.si_record_changed%type;
nr_sequencia_w			cp_indicator.nr_sequencia%type;
ds_unique_value_w		cp_indicator.ds_unique_value%type;


BEGIN

begin
	select	nr_sequencia,
			ds_version,
			ds_display_name
	into STRICT	nr_sequencia_p,
			ds_version_w,
			ds_display_name_w
	from	cp_measure
	where	nr_sequencia = (
		SELECT	max(nr_sequencia)
		from	cp_measure
		where	ds_unique_value = ds_unique_value_p
	);
exception
	when no_data_found then
	begin
		nr_sequencia_p		:= null;
		ds_version_w		:= null;
		ds_display_name_w	:= null;
	end;
end;

select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'Y' END
into STRICT	si_record_changed_w
from	cp_measure
where	(ds_version IS NOT NULL AND ds_version::text <> '')
and		ds_version <> ds_version_p;

if (ds_version_w IS NOT NULL AND ds_version_w::text <> '') and (si_record_changed_w = 'Y') then

	select	CASE WHEN count(1)=0 THEN  'Y'  ELSE 'N' END
	into STRICT	si_record_changed_w
	from	cp_measure
	where	ds_unique_value = ds_unique_value_p;
	
	if (si_record_changed_w = 'N') then
		select	CASE WHEN ds_display_name_p=coalesce(ds_display_name_w, ds_display_name_p) THEN  'N'  ELSE 'Y' END
		into STRICT	si_record_changed_w
		;
	end if;

end if;

if (coalesce(nr_sequencia_p::text, '') = '' or ds_version_w <> ds_version_p) then
	select	nextval('cp_measure_seq')
	into STRICT	nr_sequencia_p
	;
	
	case upper(ds_caption_color_p)
		when 'GREEN' then ds_caption_color_w	:= '#00BA2F';
		when 'YELLOW' then ds_caption_color_w	:= '#E3A600';
		when 'RED' then ds_caption_color_w	:= '#DA2935';
		else ds_caption_color_w			:= null;
	end case;

	insert into cp_measure(
		nr_sequencia,
		ds_unique_value,
		ds_display_name,
		ds_status_color,
		ds_version,
		nr_score,
		ie_language,
		nr_seq_order,
		ie_active,
		ie_origin,
		si_import_status,
		ie_situacao,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		si_record_changed
	) values (
		nr_sequencia_p,
		ds_unique_value_p,
		ds_display_name_p,
		ds_caption_color_w,
		ds_version_p,
		nr_score_p,
		ie_language_p,
		nr_seq_order_p,
		ie_active_p,
		'E', -- From Elsevier
		'SP',
		'I',
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		coalesce(si_record_changed_w, 'N')
	);
	
	update	cp_measure
	set		si_record_changed = 'N'
	where 	ds_version = ds_version_w;
end if;

if (nr_seq_indicator_p IS NOT NULL AND nr_seq_indicator_p::text <> '') then
	begin
		select	nr_sequencia
		into STRICT	nr_seq_indic_measure_p
		from	cp_indicator_measure
		where	nr_seq_indicator = nr_seq_indicator_p
			and nr_seq_measure = nr_sequencia_p;
	exception
		when no_data_found then
			nr_seq_indic_measure_p	:= null;
	end;
	
	if (coalesce(nr_seq_indic_measure_p::text, '') = '') then
	
		if (coalesce(si_record_changed_w, 'N') = 'N') then
		
			select	max(ds_unique_value)
			into STRICT	ds_unique_value_w
			from	cp_indicator
			where	nr_sequencia = nr_seq_indicator_p;
			
			if (ds_unique_value_w IS NOT NULL AND ds_unique_value_w::text <> '') then
			
				select	max(nr_sequencia)
				into STRICT	nr_sequencia_w
				from	cp_indicator
				where	ds_unique_value = ds_unique_value_w
				and		nr_sequencia < nr_seq_indic_measure_p;
				
				if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
				
					select	CASE WHEN count(0)='Y' THEN  'N' END
					into STRICT	si_record_changed_w
					from	cp_indicator a,
							cp_indicator_measure b
					where	a.nr_sequencia = b.nr_seq_indicator
					and		a.nr_sequencia = nr_sequencia_w
					and		b.nr_seq_goal_indic = nr_seq_goal_indic_p;
					
				end if;
				
			end if;
		end if;
		
		select	nextval('cp_indicator_measure_seq')
		into STRICT	nr_seq_indic_measure_p
		;

		insert into cp_indicator_measure(
			nr_sequencia,
			nr_seq_indicator,
			nr_seq_measure,
			ie_origin,
			si_import_status,
			ie_situacao,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_goal_indic,
			si_record_changed
		) values (
			nr_seq_indic_measure_p,
			nr_seq_indicator_p,
			nr_sequencia_p,
			'E', -- From Elsevier
			'SP',
			'I',
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_goal_indic_p,
			coalesce(si_record_changed_w, 'N')
		);
		
		update	cp_indicator_measure
		set		si_record_changed = 'N'
		where	nr_seq_indicator = nr_sequencia_w;
		
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cp_create_measure ( nr_seq_indicator_p cp_indicator_measure.nr_seq_indicator%type, ds_unique_value_p cp_measure.ds_unique_value%type, ds_display_name_p cp_measure.ds_display_name%type, ds_caption_color_p cp_measure.ds_caption_color%type, ds_version_p cp_measure.ds_version%type, nr_score_p cp_measure.nr_score%type, ie_language_p cp_measure.ie_language%type, nr_seq_order_p cp_measure.nr_seq_order%type, ie_active_p cp_measure.ie_active%type, nm_usuario_p cp_measure.nm_usuario%type, nr_sequencia_p INOUT cp_measure.nr_sequencia%type, nr_seq_indic_measure_p INOUT cp_indicator_measure.nr_sequencia%type, nr_seq_goal_indic_p cp_indicator_measure.nr_seq_goal_indic%type ) FROM PUBLIC;

