CREATE OR REPLACE PROCEDURE cp_release_progress ( nr_sequencia_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_prescr_w pe_prescr_cp_progress.nr_seq_prescr%type;


BEGIN
	select	nr_seq_prescr
	into STRICT    nr_seq_prescr_w
	from	pe_prescr_cp_progress
	where	nr_sequencia = nr_sequencia_p;

	update	pe_prescr_cp_progress
	set 	dt_liberacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_sequencia_p;

	update  pe_prescr_cp_progress
	set     dt_suspensao = clock_timestamp(),
			nm_usuario_susp = nm_usuario_p
	where	nr_seq_prescr = nr_seq_prescr_w
	and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and     nr_sequencia <> nr_sequencia_p
	and     coalesce(dt_suspensao::text, '') = '';

	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
