CREATE OR REPLACE PROCEDURE cp_remove_goal ( nr_seq_goal_p bigint ) AS $body$
BEGIN
        delete  from patient_cp_indic_plan
                where NR_SEQ_PAT_CP_IND in (SELECT nr_sequencia 
                                            from patient_cp_indicator 
                                            where NR_SEQ_PAT_CP_GOAL = nr_seq_goal_p);
        commit;

        delete  from patient_cp_indic_measure 
                where NR_SEQ_PAT_CP_IND in (SELECT nr_sequencia 
                                            from patient_cp_indicator 
                                            where NR_SEQ_PAT_CP_GOAL = nr_seq_goal_p);
        commit;

        delete  from patient_cp_indicator 
                where NR_SEQ_PAT_CP_GOAL = nr_seq_goal_p;
        commit;

        delete from PATIENT_CP_GOAL_OBS 
                where NR_SEQ_PAT_CP_GOAL = nr_seq_goal_p;
        commit;

        delete  from patient_cp_goal where nr_sequencia = nr_seq_goal_p;

        commit;

    EXCEPTION
        WHEN OTHERS THEN
            ROLLBACK;
END;
$body$
