SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cp_remove_indicator ( nr_seq_cp_indicator_p bigint, nr_seq_pat_cp_ind_p bigint ) AS $body$
DECLARE


nr_seq_goal_w     patient_cp_goal.nr_sequencia%type;
qt_reg_w          bigint;


BEGIN
    delete  from patient_cp_indic_measure 
            where NR_SEQ_PAT_CP_IND = nr_seq_pat_cp_ind_p;

    delete  from patient_cp_indic_plan 
            where NR_SEQ_PAT_CP_IND = nr_seq_pat_cp_ind_p;
            
    select nr_seq_pat_cp_goal
    into STRICT nr_seq_goal_w
    from patient_cp_indicator
    where nr_sequencia = nr_seq_pat_cp_ind_p;

    delete  from patient_cp_indicator 
            where NR_SEQ_CP_INDICATOR = nr_seq_cp_indicator_p 
            and nr_sequencia = nr_seq_pat_cp_ind_p;

    select count(nr_sequencia)
    into STRICT qt_reg_w
    from patient_cp_indicator
    where nr_seq_pat_cp_goal = nr_seq_goal_w;

    if qt_reg_w = 0 then
	  delete from PATIENT_CP_GOAL_OBS where NR_SEQ_PAT_CP_GOAL = nr_seq_goal_w;
      delete  from patient_cp_goal where nr_sequencia = nr_seq_goal_w;
    end if;

    commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
