CREATE OR REPLACE PROCEDURE cp_select_care_plan (nr_atendimento_p bigint, nr_seq_prescr_p bigint, nr_seq_care_plan_p bigint, nm_usuario_p text, selection_p text, nr_seq_patient_cp_p bigint) AS $body$
DECLARE


nr_sequencia_p_c_plan_w		patient_care_plan.nr_sequencia%type;	
cd_pessoa_fisica_w  		pessoa_fisica.cd_pessoa_fisica%type;
dt_release_w 				patient_care_plan.dt_release%type;
dt_start_w 					patient_care_plan.dt_start%type;
nr_sequencia_w				patient_care_plan.nr_sequencia%type;


BEGIN

select  a.cd_pessoa_fisica
into STRICT    cd_pessoa_fisica_w
from    atendimento_paciente a
where   a.nr_atendimento = nr_atendimento_p;

if (coalesce(nr_seq_patient_cp_p::text, '') = '') then
	begin
	select	a.nr_sequencia
	into STRICT	nr_sequencia_w
	from	patient_care_plan a
	where	a.nr_seq_care_plan = nr_seq_care_plan_p
	and		a.nr_seq_prescr	= nr_seq_prescr_p;
	exception
		when no_data_found then
			nr_sequencia_w	:= null;
	end;
else
	nr_sequencia_w	:= nr_seq_patient_cp_p;
end if;


if (coalesce(nr_sequencia_w::text, '') = '') then
	insert into patient_care_plan(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_atendimento,
		nr_seq_prescr,
		nr_seq_care_plan,
		cd_pessoa_fisica,
		dt_start,
		dt_end,
		dt_release,
		si_selected,
		ie_situacao)
	values (nextval('patient_care_plan_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_atendimento_p,
		nr_seq_prescr_p,
		nr_seq_care_plan_p,
		cd_pessoa_fisica_w,
		null,
		null,
		null,
		SELECTion_p,
		'A');
else
	update	patient_care_plan
	set		si_selected = selection_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_sequencia_w;
end if;

if (selection_p = 'N') then
	delete from patient_cp_problem where nr_seq_pat_care_plan = nr_sequencia_w;
	delete from patient_cp_goal where nr_seq_pat_care_plan = nr_sequencia_w;
	delete from pe_prescr_care_plan where nr_seq_pat_care_plan = nr_sequencia_w;
end if;

commit;

  
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
