CREATE OR REPLACE PROCEDURE cp_select_education ( nr_seq_pat_care_plan_p bigint, nr_seq_cp_goal_p bigint, nr_seq_patient_cp_goal_p bigint, nm_usuario_p text, selection_p text) AS $body$
DECLARE


nr_sequencia_pcpg_w   patient_cp_goal.nr_sequencia%type;
pcpg_count_w	      bigint;


BEGIN

begin
select	count(nr_sequencia)
into STRICT 	nr_sequencia_pcpg_w
from 	patient_cp_goal
where 	nr_seq_pat_care_plan = nr_seq_pat_care_plan_p
and 	nr_seq_cp_goal = nr_seq_cp_goal_p;
exception
	when no_data_found then
	nr_sequencia_pcpg_w	:= 0;
end;

if (nr_sequencia_pcpg_w <= 0) then
	select nextval('patient_cp_goal_seq') into STRICT nr_sequencia_pcpg_w;

	insert into PATIENT_CP_GOAL(
			  DT_ATUALIZACAO,
			  DT_ATUALIZACAO_NREC,
			  DT_END,
			  DT_RELEASE,
			  DT_START,
			  NM_USUARIO,
			  NM_USUARIO_NREC,
			  NR_SEQ_CP_GOAL,
			  NR_SEQ_PAT_CARE_PLAN,
			  SI_SELECTED,
			  NR_SEQUENCIA
	) values (
			  clock_timestamp(),
			  clock_timestamp(),
			  null,
			  null,
			  null,
			  nm_usuario_p,
			  nm_usuario_p,
			  nr_seq_cp_goal_p,
			  nr_seq_pat_care_plan_p,
			  SELECTion_p,
			  nr_sequencia_pcpg_w);
else
	update	patient_cp_goal p
	set		p.si_selected = selection_p,
			p.nm_usuario = nm_usuario_p,
			p.dt_atualizacao = clock_timestamp()
	where	p.nr_sequencia in (SELECT	a.nr_sequencia
                          from 	patient_cp_goal a
                          where 	a.nr_seq_pat_care_plan = nr_seq_pat_care_plan_p
                          and 	a.nr_seq_cp_goal = nr_seq_cp_goal_p);
end if;

    commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
