SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cp_select_interv_just ( nr_prescricao_p bigint, nr_sequencia_p bigint, si_selected text, nr_seq_prescr_proc_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_n integer;
nr_seq_exists integer;


BEGIN
nr_sequencia_n := 0;

begin
  select nr_sequencia into STRICT nr_seq_exists
  from log_care_plan 
  where NR_SEQ_PRESCR_PROC=nr_seq_prescr_proc_p;
exception
  when no_data_found then
    nr_seq_exists := null;
end;

if (si_selected <> 'Y') then

  if (nr_seq_exists IS NOT NULL AND nr_seq_exists::text <> '') then
    update log_care_plan set ds_justificativa=ds_justificativa_p
    where NR_SEQ_PRESCR_PROC=nr_seq_prescr_proc_p;
  else
    select nextval('log_care_plan_seq') into STRICT nr_sequencia_n;
    insert into log_care_plan(
              DT_ATUALIZACAO,
              NM_USUARIO,
              NR_PRESCRICAO,
              NR_SEQ_PRESCR_PROC,
              DS_JUSTIFICATIVA,
              NR_SEQUENCIA)
    values (
              clock_timestamp(),
              nm_usuario_p,
              nr_prescricao_p,
              nr_seq_prescr_proc_p,
              ds_justificativa_p,
              nr_sequencia_n);
  end if;
else
  begin
   delete from log_care_plan
   where nr_prescricao=nr_prescricao_p
   and NR_SEQ_PRESCR_PROC = nr_seq_prescr_proc_p;
  exception
    when no_data_found then
      null;
  end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
