CREATE OR REPLACE PROCEDURE cp_select_problem (nr_seq_pat_care_plan_p bigint, nr_seq_cp_problem_p bigint, selection_p text, nr_seq_pat_cp_problem_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_patient_cp_problem_w	patient_cp_problem.nr_sequencia%type;
qt_rec_w				bigint;


BEGIN

begin
select	a.nr_sequencia
into STRICT	nr_seq_patient_cp_problem_w
from	patient_cp_problem a
where 	nr_seq_pat_care_plan = nr_seq_pat_care_plan_p
and		nr_seq_cp_problem = nr_seq_cp_problem_p;
exception
	when no_data_found then
	nr_seq_patient_cp_problem_w	:= null;
end;

if (coalesce(nr_seq_patient_cp_problem_w::text, '') = '') then
	select nextval('patient_cp_problem_seq')
	into STRICT nr_seq_patient_cp_problem_w 
	;

	insert into patient_cp_problem(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_pat_care_plan,
				nr_seq_cp_problem,
				si_selected
			)
	values (
				nr_seq_patient_cp_problem_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_pat_care_plan_p,
				nr_seq_cp_problem_p,
				SELECTion_p
			);
else
	update	patient_cp_problem
	set		si_selected = selection_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_patient_cp_problem_w;
end if;

if (selection_p = 'N') then
	delete from patient_cp_goal where nr_seq_pat_cp_problem = nr_seq_patient_cp_problem_w;
	delete from patient_cp_interv_plan where nr_seq_pat_cp_problem = nr_seq_patient_cp_problem_w;
	delete from pe_prescr_problem where nr_seq_pat_cp_problem = nr_seq_patient_cp_problem_w;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
