CREATE OR REPLACE PROCEDURE create_acc_batch_receivable ( cd_estabelecimento_p bigint, ie_tipo_lote_p text, nr_seq_classif_finan_p bigint, nm_usuario_p text) AS $body$
DECLARE


					
nr_lote_contabil_w	bigint;
parametro_w	techone_pck.batchparameters_tp;

					

BEGIN

CALL wheb_usuario_pck.set_nm_usuario(nm_usuario_p);
CALL wheb_usuario_pck.set_cd_estabelecimento(cd_estabelecimento_p);
CALL philips_param_pck.set_nr_seq_idioma(9);

nr_lote_contabil_w	:= techone_pck.create_batch(	cd_estabelecimento_p,
							5,
							clock_timestamp(),
							nm_usuario_p);
							
if (nr_lote_contabil_w IS NOT NULL AND nr_lote_contabil_w::text <> '') then
					
	
	parametro_w.DS_VALOR_PARAMETRO	:= coalesce(ie_tipo_lote_p,'A');
	CALL techone_pck.update_parameters(	nr_lote_contabil_w,
					1,
					parametro_w);
	parametro_w.DS_VALOR_PARAMETRO	:= null;			
	
	
	
	if (nr_seq_classif_finan_p IS NOT NULL AND nr_seq_classif_finan_p::text <> '') then
		parametro_w.VL_PARAMETRO	:= nr_seq_classif_finan_p;
		CALL techone_pck.update_parameters(	nr_lote_contabil_w,
						2,
						parametro_w);
		parametro_w.VL_PARAMETRO	:= null;
	end if;

					
	CALL techone_pck.Generate_batch(	nr_lote_contabil_w,
					nm_usuario_p,
					'N');		

					
	CALL techone_pck.send_integration(	nr_lote_contabil_w,
					nm_usuario_p);
	
end if;

commit;

end;
$body$
