CREATE OR REPLACE PROCEDURE create_clin_note_ispm_return ( cd_prontuario_p pessoa_fisica.NR_PRONTUARIO%TYPE, nr_atendimento_p ATENDIMENTO_PACIENTE.NR_ATENDIMENTO%TYPE, ds_evolucao_p text ) AS $body$
DECLARE


cd_pessoa_fisica_w 		pessoa_fisica.CD_PESSOA_FISICA%TYPE;


BEGIN
	SELECT max(cd_pessoa_fisica)
	INTO STRICT cd_pessoa_fisica_w
	FROM pessoa_fisica 
	WHERE nr_prontuario = cd_prontuario_p;
	
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '' AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		insert into evolucao_paciente(
			cd_evolucao,
			dt_evolucao,
			cd_pessoa_fisica,
			dt_atualizacao,
			nm_usuario,
			nr_atendimento,
			ds_evolucao
			)
		values (
			nextval('evolucao_paciente_seq'),
			clock_timestamp(),
			cd_pessoa_fisica_w,
			clock_timestamp(),
			'ispm_auto',
			nr_atendimento_p,
			ds_evolucao_p);
		
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
