CREATE OR REPLACE PROCEDURE create_dimension ( nr_seq_dashboard_p dashboard_base.nr_sequencia%type, nr_seq_indicator_p ind_base.nr_sequencia%type, nr_seq_dimension_p ind_dimensao.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w			bigint	:= 0;


BEGIN

select	count(nr_sequencia)
into STRICT	qt_registro_w
from	dashboard_dimension a
where	a.nr_seq_dashboard	= nr_seq_dashboard_p
and	a.nr_seq_indicator	= nr_seq_indicator_p
and	a.nr_seq_dimension	= nr_seq_dimension_p;

if (qt_registro_w = 0) then

	insert into dashboard_dimension(
		nr_sequencia,
		nr_seq_dashboard,
		nr_seq_indicator,
		nr_seq_dimension,
		dt_atualizacao,
		nm_usuario)
	values (	nextval('dashboard_dimension_seq'),
		nr_seq_dashboard_p,
		nr_seq_indicator_p,
		nr_seq_dimension_p,
		clock_timestamp(),
		nm_usuario_p);

end if;

commit;

end;
