CREATE OR REPLACE PROCEDURE create_natural_person ( ds_usuario_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w varchar(10);



BEGIN
select	nextval('pessoa_fisica_seq')
into STRICT	cd_pessoa_fisica_w
;

insert into pessoa_fisica(
			cd_pessoa_fisica,
			ie_tipo_pessoa,
			nm_pessoa_fisica,
			dt_atualizacao,
			nm_usuario) values (
			cd_pessoa_fisica_w,
			2,
			ds_usuario_p,
			clock_timestamp(),
			nm_usuario_p );

update  usuario
set     cd_pessoa_fisica = cd_pessoa_fisica_w
where   ds_usuario  =  ds_usuario_p;

commit;

end;
$body$
