CREATE OR REPLACE PROCEDURE create_user_infos_pathway ( cd_prontuario_p w_ispm_pathway.nr_prontuario%type, cd_estabelecimento_p w_ispm_pathway.cd_estabelecimento%type, cd_pessoa_fisica_p pf_codigo_externo.cd_pessoa_fisica%type, nm_usuario_p w_ispm_pathway.nm_usuario%type) AS $body$
DECLARE


ie_pessoa_cadastrada_w	bigint;


BEGIN
	insert into w_ispm_pathway(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nm_usuario_pathway,
		cd_estabelecimento,
		nr_prontuario
	) values (
		nextval('w_ispm_pathway_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		cd_estabelecimento_p,
		cd_prontuario_p
	);
	
	select max(cd_pessoa_fisica)
	into STRICT ie_pessoa_cadastrada_w
	from pf_codigo_externo
	where cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
    and     ie_tipo_codigo_externo = 'ISPM';
		
	if (coalesce(ie_pessoa_cadastrada_w::text, '') = '') then
		insert into pf_codigo_externo(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			ie_tipo_codigo_externo,
			cd_pessoa_fisica,
            cd_pessoa_fisica_externo,
            cd_estabelecimento
		) values (
			nextval('pf_codigo_externo_seq'),
			nm_usuario_p,
			clock_timestamp(),
			'ISPM',
			cd_pessoa_fisica_p,
            0,
            cd_estabelecimento_p
		);
	end if;

commit;
end;
$body$
