SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE criar_atualizacao_versao ( cd_versao_atual_p text, nm_usuario_p text, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w					bigint;
cd_versao_anterior_w			varchar(15);
vl_job_queue_process_sis_w		bigint := 0;
vl_job_queue_process_banco_w	bigint := 0;
nr_seq_versao_w					bigint;
ie_exec_job_w					varchar(1);


BEGIN

select nextval('atualizacao_versao_seq')
into STRICT	nr_sequencia_w
;

select	max(cd_versao)
into STRICT	cd_versao_anterior_w
from	aplicacao_tasy_versao
where	cd_aplicacao_tasy	= 'Tasy'
and	dt_versao	= (
	SELECT	max(dt_versao)
	from aplicacao_tasy_versao
	where	cd_aplicacao_tasy	= 'Tasy');

begin
	EXECUTE ' select	nvl(value,0) from	v$parameter  where	name = :bind_job' INTO STRICT vl_job_queue_process_banco_w USING 'job_queue_processes';
		exception
			when others then
				vl_job_queue_process_banco_w := -1;
end;

select	max(nr_sequencia)
into STRICT	nr_seq_versao_w
from	atualizacao_versao;

select	coalesce(vl_job_queue_process,-1),
		coalesce(ie_exec_job,'N')
into STRICT	vl_job_queue_process_sis_w,
		ie_exec_job_w
from	atualizacao_versao
where	nr_sequencia = nr_seq_versao_w;

if (ie_exec_job_w = 'N') then
	begin
	if (vl_job_queue_process_sis_w = 0) or (vl_job_queue_process_sis_w < vl_job_queue_process_banco_w) then
			begin
			vl_job_queue_process_sis_w := vl_job_queue_process_banco_w;
			end;
	elsif (vl_job_queue_process_sis_w = 0) and (vl_job_queue_process_banco_w = 0) then
			begin
			vl_job_queue_process_sis_w := -1;
			end;
	end if;
	end;
end if;

if (vl_job_queue_process_sis_w <= 0) then
	begin
		ie_exec_job_w := 'N';
	end;
else
	begin
		ie_exec_job_w := 'S';
	end;
end if;

insert into atualizacao_versao(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_versao_anterior,
	cd_versao_atual,
	vl_job_queue_process,
	ie_exec_job)
values (
	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_versao_anterior_w,
	cd_versao_atual_p,
	vl_job_queue_process_sis_w,
	ie_exec_job_w);

nr_sequencia_p		:= nr_sequencia_w;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
