SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE criar_campo_visual ( nm_tabela_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_criacao_w	bigint;
nr_seq_apresent_w	bigint;

expressao1_w	varchar(255) := obter_desc_expressao_idioma(299344, null, wheb_usuario_pck.get_nr_seq_idioma);--Texto
expressao2_w	varchar(255) := obter_desc_expressao_idioma(562627, null, wheb_usuario_pck.get_nr_seq_idioma);--Texto visual
BEGIN
if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
	select	max(nr_sequencia_criacao) + 5,
		max(nr_seq_apresent) + 3
	into STRICT	nr_sequencia_criacao_w,
		nr_seq_apresent_w
	from	tabela_atributo
	where	nm_tabela	= nm_tabela_p;

	insert into tabela_atributo(
		nm_tabela,
		nm_atributo,
		ds_label,
		nr_sequencia_criacao,
		ie_obrigatorio,
		ie_tipo_atributo,
		dt_atualizacao,
		nm_usuario,
		dt_criacao,
		ie_criar_descricao,
		ie_log_exclusao,
		ie_log_update,
		nr_seq_apresent,
		ie_readonly,
		ie_tabstop,
		ie_componente,
		ie_situacao,
		ie_atualizar_versao,
		ie_criar_desc_fk,
		ds_atributo)
	values (nm_tabela_p,
		substr('DS_TEXTO'||nr_sequencia_criacao_w,1,50),
		substr(expressao1_w||nr_sequencia_criacao_w,1,50),
		nr_sequencia_criacao_w,
		'N',
		'VISUAL',
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		'N',
		'N',
		'N',
		nr_seq_apresent_w,
		'S',
		'N',
		'tx',
		'A',
		'S',
		'N',
		substr(expressao2_w,1,50));
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
