CREATE OR REPLACE PROCEDURE criar_conta_auditoria (cd_convenio_p bigint, cd_categoria_p text, dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, nr_atendimento_p bigint, nr_interno_conta_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w   		conta_paciente.cd_estabelecimento%type;
nr_interno_conta_nova_w         conta_paciente.nr_interno_conta%type;
dt_ref_valida_w			convenio.dt_ref_valida%type;


BEGIN

select nextval('conta_paciente_seq')
into STRICT nr_interno_conta_nova_w
;

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select 	dt_ref_valida
into STRICT	dt_ref_valida_w
from	convenio
where	cd_convenio = cd_convenio_p;

insert into conta_paciente(
			   nr_atendimento,
			   dt_acerto_conta,
			   ie_status_acerto,
			   dt_periodo_inicial,
			   dt_periodo_final,
			   dt_mesano_referencia,
			   nm_usuario,
			   nr_interno_conta,
			   cd_estabelecimento,
			   cd_convenio_parametro,
			   cd_categoria_parametro,
			   dt_atualizacao
			   )
			   values (
			   nr_atendimento_p,
			   clock_timestamp(),
			   1,
			   dt_periodo_inicial_p,
			   dt_periodo_final_p,
			   dt_ref_valida_w,
			   nm_usuario_p,
			   nr_interno_conta_nova_w,
			   cd_estabelecimento_w,
			   cd_convenio_p,
			   cd_categoria_p,
			   clock_timestamp()
			   );

commit;

nr_interno_conta_p := nr_interno_conta_nova_w;

end;
$body$
