SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE criar_job_dados_ult_compra () AS $body$
DECLARE


job_w 			bigint;
qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	job_v
where	upper(comando) like '%ATUALIZA_DADOS_ULTIMA_COMPRA%';

if (qt_registros_w = 0) then
	DBMS_JOB.SUBMIT(job_w, 'ATUALIZA_DADOS_ULTIMA_COMPRA;', clock_timestamp() + interval '1 days', 'TRUNC(SYSDATE + 2) + 1/1440');
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
